INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (1,'Inventory and Control of Hardware Assets','Actively manage (inventory, track, and correct) all hardware devices on the network so that only authorized devices are given access, and unauthorized and unmanaged devices are found and prevented from gaining access.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (2,'Inventory and Control of Software Assets','Actively manage (inventory, track, and correct) all software on the network so that only authorized software is installed and can execute, and that unauthorized and unmanaged software is found and prevented from installation or execution. ',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (3,'Continuous Vulnerability Management','Continuously acquire, assess, and take action on new information in order to identify vulnerabilities, remediate, and minimize the window of opportunity for attackers.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (4,'Controlled Use of Administrative Privileges','The processes and tools used to track/control/prevent/correct the use, assignment, and configuration of administrative privileges on computers, networks, and applications.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (5,'Secure Configuration for Hardware and Software on Mobile Devices, Laptops, Workstations and Servers','Establish, implement, and actively manage (track, report on, correct) the security configuration of mobile devices, laptops, servers, and workstations using a rigorous configuration management and change control process in order to prevent attackers from exploiting vulnerable services and settings. ',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (6,'Maintenance, Monitoring and Analysis of Audit Logs','Collect, manage, and analyze audit logs of events that could help detect, understand, or recover from an attack. ',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (7,'Email and Web Browser Protections','Minimize the attack surface and the opportunities for attackers to manipulate human behavior though their interaction with web browsers and email systems. ',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (8,'Malware Defenses','Control the installation, spread, and execution of malicious code at multiple points in the enterprise, while optimizing the use of automation to enable rapid updating of defense, data gathering, and corrective action.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (9,'Limitation and Control of Network Ports, Protocols, and Services','Manage (track/control/correct) the ongoing operational use of ports, protocols, and services on networked devices in order to minimize windows of vulnerability available to attackers.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (10,'Data Recovery Capabilities','The processes and tools used to properly back up critical information with a proven methodology for timely recovery of it.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (11,'Secure Configuration for Network Devices, such as Firewalls, Routers and Switches','Establish, implement, and actively manage (track, report on, correct) the security configuration of network infrastructure devices using a rigorous configuration management and change control process in order to prevent attackers from exploiting vulnerable services and settings.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (12,'Boundary Defense','Detect/prevent/correct the flow of information transferring networks of different trust levels with a focus on security-damaging data.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (13,'Data Protection','The processes and tools used to prevent data exfiltration, mitigate the effects of exfiltrated data, and ensure the privacy and integrity of sensitive information.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (14,'Controlled Access Based on the Need to Know','The processes and tools used to track/control/prevent/correct secure access to critical assets (e.g., information, resources, systems) according to the formal determination of which persons, computers, and applications have a need and right to access these critical assets based on an approved classification.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (15,'Wireless Access Control','The processes and tools used to track/control/prevent/correct the security use of wireless local area networks (WLANs), access points, and wireless client systems.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (16,'Account Monitoring and Control','Actively manage the life cycle of system and application accounts - their creation, use, dormancy, deletion - in order to minimize opportunities for attackers to leverage them.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (17,'Implement a Security Awareness and Training Program','For all functional roles in the organization (prioritizing those mission-critical to the business and its security), identify the specific knowledge, skills, and abilities needed to support defense of the enterprise; develop and execute an integrated plan to assess, identify gaps, and remediate through policy, organizational planning, training, and awareness programs.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (18,'Application Software Security','Manage the security life cycle of all in-house developed and acquired software in order to prevent, detect, and correct security weaknesses.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (19,'Incident Response and Management','Protect the organization\'s information, as well as its reputation, by developing and implementing an incident response infrastructure (e.g., plans, defined roles, training, communications, management oversight) for quickly discovering an attack and then effectively containing the damage, eradicating the attacker\'s presence, and restoring the integrity of the network and systems.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (20,'Penetration Tests and Red Team Exercises','Test the overall strength of an organization\'s defense (the technology, the processes, and the people) by simulating the objectives and actions of an attacker.',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (21,'ACCESS CONTROL POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. An access control policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the access control policy and associated access controls; and\n			b. Reviews and updates the current:\n				1. Access control policy [Assignment: organization-defined frequency]; and\n				2. Access control procedures [Assignment: organization-defined frequency].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (22,'ACCOUNT MANAGEMENT','The organization:\n			a. Identifies and selects the following types of information system accounts to support organizational missions/business functions: [Assignment: organization-defined information system account types];\n			b. Assigns account managers for information system accounts;\n			c. Establishes conditions for group and role membership;\n			d. Specifies authorized users of the information system, group and role membership, and access authorizations (i.e., privileges) and other attributes (as required) for each account;\n			e. Requires approvals by [Assignment: organization-defined personnel or roles] for requests to create information system accounts;\n			f. Creates, enables, modifies, disables, and removes information system accounts in accordance with [Assignment: organization-defined procedures or conditions];\n			g. Monitors the use of information system accounts;\n			h. Notifies account managers:\n				1. When accounts are no longer required;\n				2. When users are terminated or transferred; and\n				3. When individual information system usage or need-to-know changes;\n			i. Authorizes access to the information system based on:\n				1. A valid access authorization;\n				2. Intended system usage; and\n				3. Other attributes as required by the organization or associated missions/business functions;\n			j. Reviews accounts for compliance with account management requirements [Assignment: organization-defined frequency]; and\n			k. Establishes a process for reissuing shared/group account credentials (if deployed) when individuals are removed from the group.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (23,'ACCOUNT MANAGEMENT | AUTOMATED SYSTEM ACCOUNT MANAGEMENT','The organization employs automated mechanisms to support the management of information system accounts.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (24,'ACCOUNT MANAGEMENT | REMOVAL OF TEMPORARY / EMERGENCY ACCOUNTS','The information system automatically [Selection: removes; disables] temporary and emergency accounts after [Assignment: organization-defined time period for each type of account].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (25,'ACCOUNT MANAGEMENT | DISABLE INACTIVE ACCOUNTS','The information system automatically disables inactive accounts after [Assignment: organization-defined time period].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (26,'ACCOUNT MANAGEMENT | AUTOMATED AUDIT ACTIONS','The information system automatically audits account creation, modification, enabling, disabling, and removal actions, and notifies [Assignment: organization-defined personnel or roles].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (27,'ACCOUNT MANAGEMENT | INACTIVITY LOGOUT','The organization requires that users log out when [Assignment: organization-defined time-period of expected inactivity or description of when to log out].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (28,'ACCOUNT MANAGEMENT | DYNAMIC PRIVILEGE MANAGEMENT','The information system implements the following dynamic privilege management capabilities: [Assignment: organization-defined list of dynamic privilege management capabilities].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (29,'ACCOUNT MANAGEMENT | ROLE-BASED SCHEMES','The organization: (a) Establishes and administers privileged user accounts in accordance with a role-based access scheme that organizes allowed information system access and privileges into roles; (b) Monitors privileged role assignments; and (c) Takes [Assignment: organization-defined actions] when privileged role assignments are no longer appropriate.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (30,'ACCOUNT MANAGEMENT | DYNAMIC ACCOUNT CREATION','The information system creates [Assignment: organization-defined information system accounts] dynamically.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (31,'ACCOUNT MANAGEMENT | RESTRICTIONS ON USE OF SHARED / GROUP ACCOUNTS','The organization only permits the use of shared/group accounts that meet [Assignment: organization-defined conditions for establishing shared/group accounts].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (32,'ACCOUNT MANAGEMENT | SHARED / GROUP ACCOUNT CREDENTIAL TERMINATION','The information system terminates shared/group account credentials when members leave the group.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (33,'ACCOUNT MANAGEMENT | USAGE CONDITIONS','The information system enforces [Assignment: organization-defined circumstances and/or usage conditions] for [Assignment: organization-defined information system accounts].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (34,'ACCOUNT MANAGEMENT | ACCOUNT MONITORING / ATYPICAL USAGE','The organization:(a) Monitors information system accounts for [Assignment: organization-defined atypical usage]; and (b) Reports atypical usage of information system accounts to [Assignment: organization-defined personnel or roles].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (35,'ACCOUNT MANAGEMENT | DISABLE ACCOUNTS FOR HIGH-RISK INDIVIDUALS','The organization disables accounts of users posing a significant risk within [Assignment: organization-defined time period] of discovery of the risk.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (36,'ACCESS ENFORCEMENT','The information system enforces approved authorizations for logical access to information and system resources in accordance with applicable access control policies.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (37,'ACCESS ENFORCEMENT | RESTRICTED ACCESS TO PRIVILEGED FUNCTIONS','Withdrawn: Incorporated into AC-6.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (38,'ACCESS ENFORCEMENT | DUAL AUTHORIZATION','The information system enforces dual authorization for [Assignment: organization-defined privileged commands and/or other organization-defined actions].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (39,'ACCESS ENFORCEMENT | MANDATORY ACCESS CONTROL','The information system enforces [Assignment: organization-defined mandatory access control policy] over all subjects and objects where the policy:\n			(a) Is uniformly enforced across all subjects and objects within the boundary of the information system;\n			(b) Specifies that a subject that has been granted access to information is constrained from doing any of the following;\n				(1) Passing the information to unauthorized subjects or objects;\n				(2) Granting its privileges to other subjects;\n				(3) Changing one or more security attributes on subjects, objects, the information system, or information system components;\n				(4) Choosing the security attributes and attribute values to be associated with newly created or modified objects; or\n				(5) Changing the rules governing access control; and\n			(c) Specifies that [Assignment: organization-defined subjects] may explicitly be granted [Assignment: organization-defined privileges (i.e., they are trusted subjects)] such that they are not limited by some or all of the above constraints.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (40,'ACCESS ENFORCEMENT | DISCRETIONARY ACCESS CONTROL','The information system enforces [Assignment: organization-defined discretionary access control policy] over defined subjects and objects where the policy specifies that a subject that has been granted access to information can do one or more of the following:\n			(a) Pass the information to any other subjects or objects;\n			(b) Grant its privileges to other subjects;\n			(c) Change security attributes on subjects, objects, the information system, or the information system’s components;\n			(d) Choose the security attributes to be associated with newly created or revised objects; or\n			(e) Change the rules governing access control.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (41,'ACCESS ENFORCEMENT | SECURITY-RELEVANT INFORMATION','The information system prevents access to [Assignment: organization-defined security-relevant information] except during secure, non-operable system states.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (42,'ACCESS ENFORCEMENT | PROTECTION OF USER AND SYSTEM INFORMATION','Withdrawn: Incorporated into MP-4 and SC-28.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (43,'ACCESS ENFORCEMENT | ROLE-BASED ACCESS CONTROL','The information system enforces a role-based access control policy over defined subjects and objects and controls access based upon [Assignment: organization-defined roles and users authorized to assume such roles].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (44,'ACCESS ENFORCEMENT | REVOCATION OF ACCESS AUTHORIZATIONS','The information system enforces the revocation of access authorizations resulting from changes to the security attributes of subjects and objects based on [Assignment: organization-defined rules governing the timing of revocations of access authorizations].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (45,'ACCESS ENFORCEMENT | CONTROLLED RELEASE','The information system does not release information outside of the established system boundary unless: (a) The receiving [Assignment: organization-defined information system or system component] provides [Assignment: organization-defined security safeguards]; and (b) [Assignment: organization-defined security safeguards] are used to validate the appropriateness of the information designated for release.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (46,'ACCESS ENFORCEMENT | AUDITED OVERRIDE OF ACCESS CONTROL MECHANISMS','The organization employs an audited override of automated access control mechanisms under [Assignment: organization-defined conditions].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (47,'INFORMATION FLOW ENFORCEMENT','The information system enforces approved authorizations for controlling the flow of information within the system and between interconnected systems based on [Assignment: organization-defined information flow control policies].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (48,'INFORMATION FLOW ENFORCEMENT | OBJECT SECURITY ATTRIBUTES','The information system uses [Assignment: organization-defined security attributes] associated with [Assignment: organization-defined information, source, and destination objects] to enforce [Assignment: organization-defined information flow control policies] as a basis for flow control decisions.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (49,'INFORMATION FLOW ENFORCEMENT | PROCESSING DOMAINS','The information system uses protected processing domains to enforce [Assignment: organization-defined information flow control policies] as a basis for flow control decisions\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (50,'INFORMATION FLOW ENFORCEMENT | DYNAMIC INFORMATION FLOW CONTROL','The information system enforces dynamic information flow control based on [Assignment: organization-defined policies].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (51,'INFORMATION FLOW ENFORCEMENT | CONTENT CHECK ENCRYPTED INFORMATION','The information system prevents encrypted information from bypassing content-checking mechanisms by [Selection (one or more): decrypting the information; blocking the flow of the encrypted information; terminating communications sessions attempting to pass encrypted information; [Assignment: organization-defined procedure or method]].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (52,'INFORMATION FLOW ENFORCEMENT | EMBEDDED DATA TYPES','The information system enforces [Assignment: organization-defined limitations] on embedding data types within other data types.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (53,'INFORMATION FLOW ENFORCEMENT | METADATA','The information system enforces information flow control based on [Assignment: organization-defined metadata].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (54,'INFORMATION FLOW ENFORCEMENT | ONE-WAY FLOW MECHANISMS','The information system enforces [Assignment: organization-defined one-way information flows] using hardware mechanisms.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (55,'INFORMATION FLOW ENFORCEMENT | SECURITY POLICY FILTERS','The information system enforces information flow control using [Assignment: organization-defined security policy filters] as a basis for flow control decisions for [Assignment: organization-defined information flows].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (56,'INFORMATION FLOW ENFORCEMENT | HUMAN REVIEWS','The information system enforces the use of human reviews for [Assignment: organization-defined information flows] under the following conditions: [Assignment: organization-defined conditions].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (57,'INFORMATION FLOW ENFORCEMENT | ENABLE / DISABLE SECURITY POLICY FILTERS','The information system provides the capability for privileged administrators to enable/disable [Assignment: organization-defined security policy filters] under the following conditions: [Assignment: organization-defined conditions].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (58,'INFORMATION FLOW ENFORCEMENT | CONFIGURATION OF SECURITY POLICY FILTERS','The information system provides the capability for privileged administrators to configure [Assignment: organization-defined security policy filters] to support different security policies.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (59,'INFORMATION FLOW ENFORCEMENT | DATA TYPE IDENTIFIERS','The information system, when transferring information between different security domains, uses [Assignment: organization-defined data type identifiers] to validate data essential for information flow decisions.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (60,'INFORMATION FLOW ENFORCEMENT | DECOMPOSITION INTO POLICY-RELEVANT SUBCOMPONENTS','The information system, when transferring information between different security domains, decomposes information into [Assignment: organization-defined policy-relevant subcomponents] for submission to policy enforcement mechanisms.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (61,'INFORMATION FLOW ENFORCEMENT | SECURITY POLICY FILTER CONSTRAINTS','The information system, when transferring information between different security domains, implements [Assignment: organization-defined security policy filters] requiring fully enumerated formats that restrict data structure and content.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (62,'INFORMATION FLOW ENFORCEMENT | DETECTION OF UNSANCTIONED INFORMATION','The information system, when transferring information between different security domains, examines the information for the presence of [Assignment: organized-defined unsanctioned information] and prohibits the transfer of such information in accordance with the [Assignment: organization-defined security policy].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (63,'INFORMATION FLOW ENFORCEMENT | INFORMATION TRANSFERS ON INTERCONNECTED SYSTEMS','Withdrawn: Incorporated into AC-4.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (64,'INFORMATION FLOW ENFORCEMENT | | DOMAIN AUTHENTICATION','The information system uniquely identifies and authenticates source and destination points by [Selection (one or more): organization, system, application, individual] for information transfer.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (65,'INFORMATION FLOW ENFORCEMENT | SECURITY ATTRIBUTE BINDING','The information system binds security attributes to information using [Assignment: organization-defined binding techniques] to facilitate information flow policy enforcement.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (66,'INFORMATION FLOW ENFORCEMENT | VALIDATION OF METADATA','The information system, when transferring information between different security domains, applies the same security policy filtering to metadata as it applies to data payloads.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (67,'INFORMATION FLOW ENFORCEMENT | APPROVED SOLUTIONS','The organization employs [Assignment: organization-defined solutions in approved configurations] to control the flow of [Assignment: organization-defined information] across security domains.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (68,'INFORMATION FLOW ENFORCEMENT | PHYSICAL / LOGICAL SEPARATION OF INFORMATION FLOWS','The information system separates information flows logically or physically using [Assignment: organization-defined mechanisms and/or techniques] to accomplish [Assignment: organization-defined required separations by types of information].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (69,'INFORMATION FLOW ENFORCEMENT | ACCESS ONLY','The information system provides access from a single device to computing platforms, applications, or data residing on multiple different security domains, while preventing any information flow between the different security domains.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (70,'SEPARATION OF DUTIES','The organization: a. Separates [Assignment: organization-defined duties of individuals]; b. Documents separation of duties of individuals; and c. Defines information system access authorizations to support separation of duties.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (71,'SEPARATION OF DUTIES','The organization: a. Separates [Assignment: organization-defined duties of individuals]; b. Documents separation of duties of individuals; and c. Defines information system access authorizations to support separation of duties.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (72,'LEAST PRIVILEGE | AUTHORIZE ACCESS TO SECURITY FUNCTIONS','The organization explicitly authorizes access to [Assignment: organization-defined security functions (deployed in hardware, software, and firmware) and security-relevant information].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (73,'LEAST PRIVILEGE | NON-PRIVILEGED ACCESS FOR NONSECURITY FUNCTIONS','The organization requires that users of information system accounts, or roles, with access to [Assignment: organization-defined security functions or security-relevant information], use non-privileged accounts or roles, when accessing nonsecurity functions.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (74,'LEAST PRIVILEGE | NETWORK ACCESS TO PRIVILEGED COMMANDS','The organization authorizes network access to [Assignment: organization-defined privileged commands] only for [Assignment: organization-defined compelling operational needs] and documents the rationale for such access in the security plan for the information system.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (75,'LEAST PRIVILEGE | SEPARATE PROCESSING DOMAINS','The information system provides separate processing domains to enable finer-grained allocation of user privileges.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (76,'LEAST PRIVILEGE | PRIVILEGED ACCOUNTS','The organization restricts privileged accounts on the information system to [Assignment: organization-defined personnel or roles].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (77,'LEAST PRIVILEGE | PRIVILEGED ACCESS BY NON-ORGANIZATIONAL USERS','The organization prohibits privileged access to the information system by non-organizational users.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (78,'LEAST PRIVILEGE | REVIEW OF USER PRIVILEGES','The organization: (a) Reviews [Assignment: organization-defined frequency] the privileges assigned to [Assignment: organization-defined roles or classes of users] to validate the need for such privileges; and (b) Reassigns or removes privileges, if necessary, to correctly reflect organizational mission/business needs.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (79,'LEAST PRIVILEGE | PRIVILEGE LEVELS FOR CODE EXECUTION','The information system prevents [Assignment: organization-defined software] from executing at higher privilege levels than users executing the software\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (80,'LEAST PRIVILEGE | AUDITING USE OF PRIVILEGED FUNCTIONS','The information system audits the execution of privileged functions.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (81,'LEAST PRIVILEGE | PROHIBIT NON-PRIVILEGED USERS FROM EXECUTING PRIVILEGED FUNCTIONS','The information system prevents non-privileged users from executing privileged functions to include disabling, circumventing, or altering implemented security safeguards/countermeasures.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (82,'UNSUCCESSFUL LOGON ATTEMPTS','The information system: a. Enforces a limit of [Assignment: organization-defined number] consecutive invalid logon attempts by a user during a [Assignment: organization-defined time period]; and b. Automatically [Selection: locks the account/node for an [Assignment: organization-defined time period]; locks the account/node until released by an administrator; delays next logon prompt according to [Assignment: organization-defined delay algorithm]] when the maximum number of unsuccessful attempts is exceeded.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (83,'UNSUCCESSFUL LOGON ATTEMPTS | AUTOMATIC ACCOUNT LOCK','Withdrawn: Incorporated into AC-7.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (84,'UNSUCCESSFUL LOGON ATTEMPTS | PURGE / WIPE MOBILE DEVICE','The information system purges/wipes information from [Assignment: organization-defined mobile devices] based on [Assignment: organization-defined purging/wiping requirements/techniques] after [Assignment: organization-defined number] consecutive, unsuccessful device logon attempts.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (85,'SYSTEM USE NOTIFICATION','The information system:\n			a. Displays to users [Assignment: organization-defined system use notification message or banner] before granting access to the system that provides privacy and security notices consistent with applicable federal laws, Executive Orders, directives, policies, regulations, standards, and guidance and states that:\n				1. Users are accessing a U.S. Government information system;\n				2. Information system usage may be monitored, recorded, and subject to audit;\n				3. Unauthorized use of the information system is prohibited and subject to criminal and civil penalties; and\n				4. Use of the information system indicates consent to monitoring and recording;\n			b. Retains the notification message or banner on the screen until users acknowledge the usage conditions and take explicit actions to log on to or further access the information system; and\n			c. For publicly accessible systems:\n				1. Displays system use information [Assignment: organization-defined conditions], before granting further access;\n				2. Displays references, if any, to monitoring, recording, or auditing that are consistent with privacy accommodations for such systems that generally prohibit those activities; and\n				3. Includes a description of the authorized uses of the system.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (86,'PREVIOUS LOGON (ACCESS) NOTIFICATION','The information system notifies the user, upon successful logon (access) to the system, of the date and time of the last logon (access).\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (87,'PREVIOUS LOGON NOTIFICATION | UNSUCCESSFUL LOGONS','The information system notifies the user, upon successful logon/access, of the number of unsuccessful logon/access attempts since the last successful logon/access.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (88,'PREVIOUS LOGON NOTIFICATION | SUCCESSFUL / UNSUCCESSFUL LOGONS','The information system notifies the user of the number of [Selection: successful logons/accesses; unsuccessful logon/access attempts; both] during [Assignment: organization-defined time period].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (89,'PREVIOUS LOGON NOTIFICATION | NOTIFICATION OF ACCOUNT CHANGES','The information system notifies the user of changes to [Assignment: organization-defined security-related characteristics/parameters of the user’s account] during [Assignment: organization-defined time period].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (90,'PREVIOUS LOGON NOTIFICATION | ADDITIONAL LOGON INFORMATION','The information system notifies the user, upon successful logon (access), of the following additional information: [Assignment: organization-defined information to be included in addition to the date and time of the last logon (access)].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (91,'CONCURRENT SESSION CONTROL','The information system limits the number of concurrent sessions for each [Assignment: organization-defined account and/or account type] to [Assignment: organization-defined number].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (92,'SESSION LOCK','The information system: a. Prevents further access to the system by initiating a session lock after [Assignment: organization-defined time period] of inactivity or upon receiving a request from a user; and b. Retains the session lock until the user reestablishes access using established identification and authentication procedures.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (93,'SESSION LOCK | PATTERN-HIDING DISPLAYS','The information system conceals, via the session lock, information previously visible on the display with a publicly viewable image.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (94,'SESSION TERMINATION','The information system automatically terminates a user session after [Assignment: organization-defined conditions or trigger events requiring session disconnect].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (95,'SESSION TERMINATION | USER-INITIATED LOGOUTS / MESSAGE DISPLAYS','The information system: (a) Provides a logout capability for user-initiated communications sessions whenever authentication is used to gain access to [Assignment: organization-defined information resources]; and (b) Displays an explicit logout message to users indicating the reliable termination of authenticated communications sessions.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (96,'SUPERVISION AND REVIEW — ACCESS CONTROL','Withdrawn: Incorporated into AC-2 and AU-6.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (97,'PERMITTED ACTIONS WITHOUT IDENTIFICATION OR AUTHENTICATION','The organization: a. Identifies [Assignment: organization-defined user actions] that can be performed on the information system without identification or authentication consistent with organizational missions/business functions; and b. Documents and provides supporting rationale in the security plan for the information system, user actions not requiring identification or authentication.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (98,'PERMITTED ACTIONS WITHOUT IDENTIFICATION OR AUTHENTICATION | NECESSARY USES','Withdrawn: Incorporated into AC-14.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (99,'AUTOMATED MARKING','Withdrawn: Incorporated into MP-3.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (100,'SECURITY ATTRIBUTES','The organization: a. Provides the means to associate [Assignment: organization-defined types of security attributes] having [Assignment: organization-defined security attribute values] with information in storage, in process, and/or in transmission; b. Ensures that the security attribute associations are made and retained with the information; c. Establishes the permitted [Assignment: organization-defined security attributes] for [Assignment: organization-defined information systems]; and d. Determines the permitted [Assignment: organization-defined values or ranges] for each of the established security attributes.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (101,'SECURITY ATTRIBUTES | DYNAMIC ATTRIBUTE ASSOCIATION','The information system dynamically associates security attributes with [Assignment: organization-defined subjects and objects] in accordance with [Assignment: organization-defined security policies] as information is created and combined.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (102,'SECURITY ATTRIBUTES | ATTRIBUTE VALUE CHANGES BY AUTHORIZED INDIVIDUALS','The information system provides authorized individuals (or processes acting on behalf of individuals) the capability to define or change the value of associated security attributes.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (103,'SECURITY ATTRIBUTES | MAINTENANCE OF ATTRIBUTE ASSOCIATIONS BY INFORMATION SYSTEM','The information system maintains the association and integrity of [Assignment: organization-defined security attributes] to [Assignment: organization-defined subjects and objects].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (104,'SECURITY ATTRIBUTES | ASSOCIATION OF ATTRIBUTES BY AUTHORIZED INDIVIDUALS','The information system supports the association of [Assignment: organization-defined security attributes] with [Assignment: organization-defined subjects and objects] by authorized individuals (or processes acting on behalf of individuals).\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (105,'SECURITY ATTRIBUTES | ATTRIBUTE DISPLAYS FOR OUTPUT DEVICES','The information system displays security attributes in human-readable form on each object that the system transmits to output devices to identify [Assignment: organization-identified special dissemination, handling, or distribution instructions] using [Assignment: organization-identified human-readable, standard naming conventions].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (106,'SECURITY ATTRIBUTES | MAINTENANCE OF ATTRIBUTE ASSOCIATION BY ORGANIZATION','The organization allows personnel to associate, and maintain the association of [Assignment: organization-defined security attributes] with [Assignment: organization-defined subjects and objects] in accordance with [Assignment: organization-defined security policies].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (107,'SECURITY ATTRIBUTES | CONSISTENT ATTRIBUTE INTERPRETATION','The organization provides a consistent interpretation of security attributes transmitted between distributed information system components.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (108,'SECURITY ATTRIBUTES | ASSOCIATION TECHNIQUES / TECHNOLOGIES','The information system implements [Assignment: organization-defined techniques or technologies] with [Assignment: organization-defined level of assurance] in associating security attributes to information.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (109,'SECURITY ATTRIBUTES | ATTRIBUTE REASSIGNMENT','The organization ensures that security attributes associated with information are reassigned only via re-grading\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (110,'SECURITY ATTRIBUTES | ATTRIBUTE CONFIGURATION BY AUTHORIZED INDIVIDUALS','The information system provides authorized individuals the capability to define or change the type and value of security attributes available for association with subjects and objects.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (111,'REMOTE ACCESS','The organization: a. Establishes and documents usage restrictions, configuration/connection requirements, and implementation guidance for each type of remote access allowed; and b. Authorizes remote access to the information system prior to allowing such connections.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (112,'REMOTE ACCESS | AUTOMATED MONITORING / CONTROL','The information system monitors and controls remote access methods.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (113,'REMOTE ACCESS | PROTECTION OF CONFIDENTIALITY / INTEGRITY USING ENCRYPTION','The information system implements cryptographic mechanisms to protect the confidentiality and integrity of remote access sessions.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (114,'REMOTE ACCESS | MANAGED ACCESS CONTROL POINTS','The information system routes all remote accesses through [Assignment: organization-defined number] managed network access control points.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (115,'REMOTE ACCESS | PRIVILEGED COMMANDS / ACCESS','The organization: (a) Authorizes the execution of privileged commands and access to security-relevant information via remote access only for [Assignment: organization-defined needs]; and (b) Documents the rationale for such access in the security plan for the information system.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (116,'REMOTE ACCESS | MONITORING FOR UNAUTHORIZED CONNECTIONS','Withdrawn: Incorporated into SI-4.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (117,'REMOTE ACCESS | PROTECTION OF INFORMATION','The organization ensures that users protect information about remote access mechanisms from unauthorized use and disclosure.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (118,'REMOTE ACCESS | ADDITIONAL PROTECTION FOR SECURITY FUNCTION ACCESS','Withdrawn: Incorporated into AC-3 (10).\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (119,'REMOTE ACCESS | DISABLE NONSECURE NETWORK PROTOCOLS','Withdrawn: Incorporated into CM-7.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (120,'REMOTE ACCESS | DISCONNECT / DISABLE ACCESS','The organization provides the capability to expeditiously disconnect or disable remote access to the information system within [Assignment: organization-defined time period].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (121,'WIRELESS ACCESS','The organization: a. Establishes usage restrictions, configuration/connection requirements, and implementation guidance for wireless access; and b. Authorizes wireless access to the information system prior to allowing such connections.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (122,'WIRELESS ACCESS | AUTHENTICATION AND ENCRYPTION','The information system protects wireless access to the system using authentication of [Selection (one or more): users; devices] and encryption.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (123,'WIRELESS ACCESS | MONITORING UNAUTHORIZED CONNECTIONS','Withdrawn: Incorporated into SI-4.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (124,'WIRELESS ACCESS | DISABLE WIRELESS NETWORKING','The organization disables, when not intended for use, wireless networking capabilities internally embedded within information system components prior to issuance and deployment.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (125,'WIRELESS ACCESS | RESTRICT CONFIGURATIONS BY USERS','The organization identifies and explicitly authorizes users allowed to independently configure wireless networking capabilities.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (126,'WIRELESS ACCESS | ANTENNAS / TRANSMISSION POWER LEVELS','The organization selects radio antennas and calibrates transmission power levels to reduce the probability that usable signals can be received outside of organization-controlled boundaries.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (127,'ACCESS CONTROL FOR MOBILE DEVICES','The organization: a. Establishes usage restrictions, configuration requirements, connection requirements, and implementation guidance for organization-controlled mobile devices; and b. Authorizes the connection of mobile devices to organizational information systems.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (128,'ACCESS CONTROL FOR MOBILE DEVICES | USE OF WRITABLE / PORTABLE STORAGE DEVICES','Withdrawn: Incorporated into MP-7.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (129,'ACCESS CONTROL FOR MOBILE DEVICES | USE OF PERSONALLY OWNED PORTABLE STORAGE DEVICES','Withdrawn: Incorporated into MP-7.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (130,'ACCESS CONTROL FOR MOBILE DEVICES | USE OF PORTABLE STORAGE DEVICES WITH NO IDENTIFIABLE OWNER','Withdrawn: Incorporated into MP-7.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (131,'ACCESS CONTROL FOR MOBILE DEVICES | RESTRICTIONS FOR CLASSIFIED INFORMATION','The organization:\n			(a) Prohibits the use of unclassified mobile devices in facilities containing information systems processing, storing, or transmitting classified information unless specifically permitted by the authorizing official; and\n			(b) Enforces the following restrictions on individuals permitted by the authorizing official to use unclassified mobile devices in facilities containing information systems processing, storing, or transmitting classified information:\n				(1) Connection of unclassified mobile devices to classified information systems is prohibited;\n				(2) Connection of unclassified mobile devices to unclassified information systems requires approval from the authorizing official;\n				(3) Use of internal or external modems or wireless interfaces within the unclassified mobile devices is prohibited; and\n				(4) Unclassified mobile devices and the information stored on those devices are subject to random reviews and inspections by [Assignment: organization-defined security officials], and if classified information is found, the incident handling policy is followed.\n			(c) Restricts the connection of classified mobile devices to classified information systems in accordance with [Assignment: organization-defined security policies].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (132,'ACCESS CONTROL FOR MOBILE DEVICES | FULL DEVICE / CONTAINER-BASED ENCRYPTION','The organization employs [Selection: full-device encryption; container encryption] to protect the confidentiality and integrity of information on [Assignment: organization-defined mobile devices].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (133,'USE OF EXTERNAL INFORMATION SYSTEMS','The organization establishes terms and conditions, consistent with any trust relationships established with other organizations owning, operating, and/or maintaining external information systems, allowing authorized individuals to: a. Access the information system from external information systems; and b. Process, store, or transmit organization-controlled information using external information systems.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (134,'USE OF EXTERNAL INFORMATION SYSTEMS | LIMITS ON AUTHORIZED USE','The organization permits authorized individuals to use an external information system to access the information system or to process, store, or transmit organization-controlled information only when the organization: (a) Verifies the implementation of required security controls on the external system as specified in the organization’s information security policy and security plan; or (b) Retains approved information system connection or processing agreements with the organizational entity hosting the external information system.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (135,'USE OF EXTERNAL INFORMATION SYSTEMS | PORTABLE STORAGE DEVICES','The organization [Selection: restricts; prohibits] the use of organization-controlled portable storage devices by authorized individuals on external information systems.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (136,'USE OF EXTERNAL INFORMATION SYSTEMS | NON-ORGANIZATIONALLY OWNED SYSTEMS / COMPONENTS / DEVICES','The organization [Selection: restricts; prohibits] the use of non-organizationally owned information systems, system components, or devices to process, store, or transmit organizational information.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (137,'USE OF EXTERNAL INFORMATION SYSTEMS | NETWORK ACCESSIBLE STORAGE DEVICES','The organization prohibits the use of [Assignment: organization-defined network accessible storage devices] in external information systems.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (138,'INFORMATION SHARING','The organization: a. Facilitates information sharing by enabling authorized users to determine whether access authorizations assigned to the sharing partner match the access restrictions on the information for [Assignment: organization-defined information sharing circumstances where user discretion is required]; and b. Employs [Assignment: organization-defined automated mechanisms or manual processes] to assist users in making information sharing/collaboration decisions.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (139,'INFORMATION SHARING | AUTOMATED DECISION SUPPORT','The information system enforces information-sharing decisions by authorized users based on access authorizations of sharing partners and access restrictions on information to be shared.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (140,'INFORMATION SHARING | INFORMATION SEARCH AND RETRIEVAL','The information system implements information search and retrieval services that enforce [Assignment: organization-defined information sharing restrictions].\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (141,'PUBLICLY ACCESSIBLE CONTENT','The organization: a. Designates individuals authorized to post information onto a publicly accessible information system; b. Trains authorized individuals to ensure that publicly accessible information does not contain nonpublic information; c. Reviews the proposed content of information prior to posting onto the publicly accessible information system to ensure that nonpublic information is not included; and d. Reviews the content on the publicly accessible information system for nonpublic information [Assignment: organization-defined frequency] and removes such information, if discovered.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (142,'DATA MINING PROTECTION','The organization employs [Assignment: organization-defined data mining prevention and detection techniques] for [Assignment: organization-defined data storage objects] to adequately detect and protect against data mining.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (143,'ACCESS CONTROL DECISIONS','The organization establishes procedures to ensure [Assignment: organization-defined access control decisions] are applied to each access request prior to access enforcement.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (144,'ACCESS CONTROL DECISIONS | TRANSMIT ACCESS AUTHORIZATION INFORMATION','The information system transmits [Assignment: organization-defined access authorization information] using [Assignment: organization-defined security safeguards] to [Assignment: organization-defined information systems] that enforce access control decisions.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (145,'ACCESS CONTROL DECISIONS | NO USER OR PROCESS IDENTITY','The information system enforces access control decisions based on [Assignment: organization-defined security attributes] that do not include the identity of the user or process acting on behalf of the user.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (146,'REFERENCE MONITOR','The information system implements a reference monitor for [Assignment: organization-defined access control policies] that is tamperproof, always invoked, and small enough to be subject to analysis and testing, the completeness of which can be assured.\n		',NULL,1);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (147,'SECURITY AWARENESS AND TRAINING POLICY AND PROCEDURES','The information system implements a reference monitor for [Assignment: organization-defined access control policies] that is tamperproof, always invoked, and small enough to be subject to analysis and testing, the completeness of which can be assured.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (148,'SECURITY AWARENESS AND TRAINING POLICY AND PROCEDURES','The organization provides basic security awareness training to information system users (including managers, senior executives, and contractors): a. As part of initial training for new users; b. When required by information system changes; and c. [Assignment: organization-defined frequency] thereafter.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (149,'SECURITY AWARENESS | PRACTICAL EXERCISES','The organization includes practical exercises in security awareness training that simulate actual cyber attacks.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (150,'SECURITY AWARENESS | INSIDER THREAT','The organization includes security awareness training on recognizing and reporting potential indicators of insider threat.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (151,'ROLE-BASED SECURITY TRAINING','The organization provides role-based security training to personnel with assigned security roles and responsibilities: a. Before authorizing access to the information system or performing assigned duties; b. When required by information system changes; and c. [Assignment: organization-defined frequency] thereafter.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (152,'ROLE-BASED SECURITY TRAINING | ENVIRONMENTAL CONTROLS','The organization provides [Assignment: organization-defined personnel or roles] with initial and [Assignment: organization-defined frequency] training in the employment and operation of environmental controls.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (153,'ROLE-BASED SECURITY TRAINING | PHYSICAL SECURITY CONTROLS','The organization provides [Assignment: organization-defined personnel or roles] with initial and [Assignment: organization-defined frequency] training in the employment and operation of physical security controls.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (154,'ROLE-BASED SECURITY TRAINING | PRACTICAL EXERCISES','The organization includes practical exercises in security training that reinforce training objectives.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (155,'ROLE-BASED SECURITY TRAINING | SUSPICIOUS COMMUNICATIONS AND ANOMALOUS SYSTEM BEHAVIOR','The organization provides training to its personnel on [Assignment: organization-defined indicators of malicious code] to recognize suspicious communications and anomalous behavior in organizational information systems.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (156,'SECURITY TRAINING RECORDS','The organization: a. Documents and monitors individual information system security training activities including basic security awareness training and specific information system security training; and b. Retains individual training records for [Assignment: organization-defined time period].\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (157,'CONTACTS WITH SECURITY GROUPS AND ASSOCIATIONS','Withdrawn: Incorporated into PM-15.\n		',NULL,2);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (158,'AUDIT AND ACCOUNTABILITY POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. An audit and accountability policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the audit and accountability policy and associated audit and accountability controls; and\n			b. Reviews and updates the current:\n				1. Audit and accountability policy [Assignment: organization-defined frequency]; and\n				2. Audit and accountability procedures [Assignment: organization-defined frequency].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (159,'AUDIT EVENTS',' The organization:\n			a. Determines that the information system is capable of auditing the following events: [Assignment: organization-defined auditable events];\n			b. Coordinates the security audit function with other organizational entities requiring audit-related information to enhance mutual support and to help guide the selection of auditable events;\n			c. Provides a rationale for why the auditable events are deemed to be adequate to support after-the-fact investigations of security incidents; and\n			d. Determines that the following events are to be audited within the information system: [Assignment: organization-defined audited events (the subset of the auditable events defined in AU-2 a.) along with the frequency of (or situation requiring) auditing for each identified event].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (160,'AUDIT EVENTS | COMPILATION OF AUDIT RECORDS FROM MULTIPLE SOURCES','Withdrawn: Incorporated into AU-12.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (161,'AUDIT EVENTS | SELECTION OF AUDIT EVENTS BY COMPONENT','Withdrawn: Incorporated into AU-12.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (162,'AUDIT EVENTS | REVIEWS AND UPDATES','The organization reviews and updates the audited events [Assignment: organization-defined frequency].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (163,'AUDIT EVENTS | PRIVILEGED FUNCTIONS','Withdrawn: Incorporated into AC-6 (9).\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (164,'CONTENT OF AUDIT RECORDS','The information system generates audit records containing information that establishes what type of event occurred, when the event occurred, where the event occurred, the source of the event, the outcome of the event, and the identity of any individuals or subjects associated with the event.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (165,'CONTENT OF AUDIT RECORDS | ADDITIONAL AUDIT INFORMATION','The information system generates audit records containing the following additional information: [Assignment: organization-defined additional, more detailed information].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (166,'CONTENT OF AUDIT RECORDS | CENTRALIZED MANAGEMENT OF PLANNED AUDIT RECORD CONTENT','The information system provides centralized management and configuration of the content to be captured in audit records generated by [Assignment: organization-defined information system components].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (167,'AUDIT STORAGE CAPACITY','The organization allocates audit record storage capacity in accordance with [Assignment: organization-defined audit record storage requirements].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (168,'AUDIT STORAGE CAPACITY | TRANSFER TO ALTERNATE STORAGE','The information system off-loads audit records [Assignment: organization-defined frequency] onto a different system or media than the system being audited.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (169,'RESPONSE TO AUDIT PROCESSING FAILURES','The information system: a. Alerts [Assignment: organization-defined personnel or roles] in the event of an audit processing failure; and b. Takes the following additional actions: [Assignment: organization-defined actions to be taken (e.g., shut down information system, overwrite oldest audit records, stop generating audit records)].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (170,'RESPONSE TO AUDIT PROCESSING FAILURES | AUDIT STORAGE CAPACITY','The information system provides a warning to [Assignment: organization-defined personnel, roles, and/or locations] within [Assignment: organization-defined time period] when allocated audit record storage volume reaches [Assignment: organization-defined percentage] of repository maximum audit record storage capacity.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (171,'RESPONSE TO AUDIT PROCESSING FAILURES | REAL-TIME ALERTS','The information system provides an alert in [Assignment: organization-defined real-time period] to [Assignment: organization-defined personnel, roles, and/or locations] when the following audit failure events occur: [Assignment: organization-defined audit failure events requiring real-time alerts].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (172,'RESPONSE TO AUDIT PROCESSING FAILURES | CONFIGURABLE TRAFFIC VOLUME THRESHOLDS','The information system enforces configurable network communications traffic volume thresholds reflecting limits on auditing capacity and [Selection: rejects; delays] network traffic above those thresholds.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (173,'RESPONSE TO AUDIT PROCESSING FAILURES | SHUTDOWN ON FAILURE','The information system invokes a [Selection: full system shutdown; partial system shutdown; degraded operational mode with limited mission/business functionality available] in the event of [Assignment: organization-defined audit failures], unless an alternate audit capability exists.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (174,'AUDIT REVIEW, ANALYSIS, AND REPORTING','The organization: a. Reviews and analyzes information system audit records [Assignment: organization-defined frequency] for indications of [Assignment: organization-defined inappropriate or unusual activity]; and b. Reports findings to [Assignment: organization-defined personnel or roles].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (175,'AUDIT REVIEW, ANALYSIS, AND REPORTING | PROCESS INTEGRATION','The organization employs automated mechanisms to integrate audit review, analysis, and reporting processes to support organizational processes for investigation and response to suspicious activities.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (176,'AUDIT REVIEW, ANALYSIS, AND REPORTING | AUTOMATED SECURITY ALERTS','Withdrawn: Incorporated into SI-4.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (177,'AUDIT REVIEW, ANALYSIS, AND REPORTING | CORRELATE AUDIT REPOSITORIES','The organization analyzes and correlates audit records across different repositories to gain organization-wide situational awareness.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (178,'AUDIT REVIEW, ANALYSIS, AND REPORTING | CENTRAL REVIEW AND ANALYSIS','The information system provides the capability to centrally review and analyze audit records from multiple components within the system.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (179,'AUDIT REVIEW, ANALYSIS, AND REPORTING | INTEGRATION / SCANNING AND MONITORING CAPABILITIES','The organization integrates analysis of audit records with analysis of [Selection (one or more): vulnerability scanning information; performance data; information system monitoring information; [Assignment: organization-defined data/information collected from other sources]] to further enhance the ability to identify inappropriate or unusual activity.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (180,'AUDIT REVIEW, ANALYSIS, AND REPORTING | CORRELATION WITH PHYSICAL MONITORING','The organization correlates information from audit records with information obtained from monitoring physical access to further enhance the ability to identify suspicious, inappropriate, unusual, or malevolent activity.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (181,'AUDIT REVIEW, ANALYSIS, AND REPORTING | PERMITTED ACTIONS','The organization specifies the permitted actions for each [Selection (one or more): information system process; role; user] associated with the review, analysis, and reporting of audit information.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (182,'AUDIT REVIEW, ANALYSIS, AND REPORTING | FULL TEXT ANALYSIS OF PRIVILEGED COMMANDS','The organization performs a full text analysis of audited privileged commands in a physically distinct component or subsystem of the information system, or other information system that is dedicated to that analysis.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (183,'AUDIT REVIEW, ANALYSIS, AND REPORTING | CORRELATION WITH INFORMATION FROM NONTECHNICAL SOURCES','The organization correlates information from nontechnical sources with audit information to enhance organization-wide situational awareness.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (184,'AUDIT REVIEW, ANALYSIS, AND REPORTING | AUDIT LEVEL ADJUSTMENT','The organization adjusts the level of audit review, analysis, and reporting within the information system when there is a change in risk based on law enforcement information, intelligence information, or other credible sources of information.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (185,'AUDIT REDUCTION AND REPORT GENERATION','The information system provides an audit reduction and report generation capability that: a. Supports on-demand audit review, analysis, and reporting requirements and after-the-fact investigations of security incidents; and b. Does not alter the original content or time ordering of audit records.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (186,'AUDIT REDUCTION AND REPORT GENERATION | AUTOMATIC PROCESSING','The information system provides the capability to process audit records for events of interest based on [Assignment: organization-defined audit fields within audit records].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (187,'AUDIT REDUCTION AND REPORT GENERATION | AUTOMATIC SORT AND SEARCH','The information system provides the capability to sort and search audit records for events of interest based on the content of [Assignment: organization-defined audit fields within audit records].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (188,'TIME STAMPS','The information system: a. Uses internal system clocks to generate time stamps for audit records; and b. Records time stamps for audit records that can be mapped to Coordinated Universal Time (UTC) or Greenwich Mean Time (GMT) and meets [Assignment: organization-defined granularity of time measurement].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (189,'TIME STAMPS | SYNCHRONIZATION WITH AUTHORITATIVE TIME SOURCE','The information system: (a) Compares the internal information system clocks [Assignment: organization-defined frequency] with [Assignment: organization-defined authoritative time source]; and (b) Synchronizes the internal system clocks to the authoritative time source when the time difference is greater than [Assignment: organization-defined time period].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (190,'TIME STAMPS | SECONDARY AUTHORITATIVE TIME SOURCE','The information system identifies a secondary authoritative time source that is located in a different geographic region than the primary authoritative time source.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (191,'PROTECTION OF AUDIT INFORMATION','The information system protects audit information and audit tools from unauthorized access, modification, and deletion.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (192,'PROTECTION OF AUDIT INFORMATION | HARDWARE WRITE-ONCE MEDIA','The information system writes audit trails to hardware-enforced, write-once media.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (193,'PROTECTION OF AUDIT INFORMATION | AUDIT BACKUP ON SEPARATE PHYSICAL SYSTEMS / COMPONENTS','The information system backs up audit records [Assignment: organization-defined frequency] onto a physically different system or system component than the system or component being audited.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (194,'PROTECTION OF AUDIT INFORMATION | CRYPTOGRAPHIC PROTECTION','The information system implements cryptographic mechanisms to protect the integrity of audit information and audit tools.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (195,'PROTECTION OF AUDIT INFORMATION | ACCESS BY SUBSET OF PRIVILEGED USERS','The organization authorizes access to management of audit functionality to only [Assignment: organization-defined subset of privileged users].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (196,'PROTECTION OF AUDIT INFORMATION | DUAL AUTHORIZATION','The organization enforces dual authorization for [Selection (one or more): movement; deletion] of [Assignment: organization-defined audit information].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (197,'PROTECTION OF AUDIT INFORMATION | READ ONLY ACCESS','The organization authorizes read-only access to audit information to [Assignment: organization-defined subset of privileged users].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (198,'NON-REPUDIATION','The information system protects against an individual (or process acting on behalf of an individual) falsely denying having performed [Assignment: organization-defined actions to be covered by non-repudiation].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (199,'NON-REPUDIATION | ASSOCIATION OF IDENTITIES','The information system: (a) Binds the identity of the information producer with the information to [Assignment: organization-defined strength of binding]; and (b) Provides the means for authorized individuals to determine the identity of the producer of the information.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (200,'NON-REPUDIATION | VALIDATE BINDING OF INFORMATION PRODUCER IDENTITY','The information system: (a) Validates the binding of the information producer identity to the information at [Assignment: organization-defined frequency]; and (b) Performs [Assignment: organization-defined actions] in the event of a validation error.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (201,'NON-REPUDIATION | CHAIN OF CUSTODY','The information system maintains reviewer/releaser identity and credentials within the established chain of custody for all information reviewed or released.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (202,'NON-REPUDIATION | VALIDATE BINDING OF INFORMATION REVIEWER IDENTITY','The information system: (a) Validates the binding of the information reviewer identity to the information at the transfer or release points prior to release/transfer between [Assignment: organization-defined security domains]; and (b) Performs [Assignment: organization-defined actions] in the event of a validation error.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (203,'NON-REPUDIATION | DIGITAL SIGNATURES','Withdrawn: Incorporated into SI-7.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (204,'AUDIT RECORD RETENTION','The organization retains audit records for [Assignment: organization-defined time period consistent with records retention policy] to provide support for after-the-fact investigations of security incidents and to meet regulatory and organizational information retention requirements.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (205,'AUDIT RECORD RETENTION | LONG-TERM RETRIEVAL CAPABILITY','The organization employs [Assignment: organization-defined measures] to ensure that long-term audit records generated by the information system can be retrieved.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (206,'AUDIT GENERATION','The information system: a. Provides audit record generation capability for the auditable events defined in AU-2 a. at [Assignment: organization-defined information system components];b. Allows [Assignment: organization-defined personnel or roles] to select which auditable events are to be audited by specific components of the information system; and c. Generates audit records for the events defined in AU-2 d. with the content defined in AU-3.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (207,'AUDIT GENERATION | SYSTEM-WIDE / TIME-CORRELATED AUDIT TRAIL','The information system compiles audit records from [Assignment: organization-defined information system components] into a system-wide (logical or physical) audit trail that is time-correlated to within [Assignment: organization-defined level of tolerance for the relationship between time stamps of individual records in the audit trail].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (208,'AUDIT GENERATION | STANDARDIZED FORMATS','The information system produces a system-wide (logical or physical) audit trail composed of audit records in a standardized format.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (209,'AUDIT GENERATION | CHANGES BY AUTHORIZED INDIVIDUALS','The information system provides the capability for [Assignment: organization-defined individuals or roles] to change the auditing to be performed on [Assignment: organization-defined information system components] based on [Assignment: organization-defined selectable event criteria] within [Assignment: organization-defined time thresholds].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (210,'MONITORING FOR INFORMATION DISCLOSURE','The organization monitors [Assignment: organization-defined open source information and/or information sites] [Assignment: organization-defined frequency] for evidence of unauthorized disclosure of organizational information.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (211,'MONITORING FOR INFORMATION DISCLOSURE | USE OF AUTOMATED TOOLS','The organization employs automated mechanisms to determine if organizational information has been disclosed in an unauthorized manner.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (212,'MONITORING FOR INFORMATION DISCLOSURE | REVIEW OF MONITORED SITES','The organization reviews the open source information sites being monitored [Assignment: organization-defined frequency].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (213,'SESSION AUDIT','The information system provides the capability for authorized users to select a user session to capture/record or view/hear.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (214,'SESSION AUDIT | SYSTEM START-UP','The information system initiates session audits at system start-up.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (215,'SESSION AUDIT | CAPTURE/RECORD AND LOG CONTENT','The information system provides the capability for authorized users to capture/record and log content related to a user session.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (216,'SESSION AUDIT | REMOTE VIEWING / LISTENING','The information system provides the capability for authorized users to remotely view/hear all content related to an established user session in real time.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (217,'ALTERNATE AUDIT CAPABILITY','The organization provides an alternate audit capability in the event of a failure in primary audit capability that provides [Assignment: organization-defined alternate audit functionality].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (218,'CROSS-ORGANIZATIONAL AUDITING','The organization employs [Assignment: organization-defined methods] for coordinating [Assignment: organization-defined audit information] among external organizations when audit information is transmitted across organizational boundaries.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (219,'CROSS-ORGANIZATIONAL AUDITING | IDENTITY PRESERVATION','The organization requires that the identity of individuals be preserved in cross-organizational audit trails.\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (220,'CROSS-ORGANIZATIONAL AUDITING | SHARING OF AUDIT INFORMATION','The organization provides cross-organizational audit information to [Assignment: organization-defined organizations] based on [Assignment: organization-defined cross-organizational sharing agreements].\n		',NULL,3);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (221,'SECURITY ASSESSMENT AND AUTHORIZATION POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A security assessment and authorization policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the security assessment and authorization policy and associated security assessment and authorization controls; and\n			b. Reviews and updates the current:\n				1. Security assessment and authorization policy [Assignment: organization-defined frequency]; and\n				2. Security assessment and authorization procedures [Assignment: organization-defined frequency].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (222,'SECURITY ASSESSMENTS','The organization:\n			a. Develops a security assessment plan that describes the scope of the assessment including:\n				1. Security controls and control enhancements under assessment;\n				2. Assessment procedures to be used to determine security control effectiveness; and\n				3. Assessment environment, assessment team, and assessment roles and responsibilities;\n			b. Assesses the security controls in the information system and its environment of operation [Assignment: organization-defined frequency] to determine the extent to which the controls are implemented correctly, operating as intended, and producing the desired outcome with respect to meeting established security requirements;\n			c. Produces a security assessment report that documents the results of the assessment; and\n			d. Provides the results of the security control assessment to [Assignment: organization-defined individuals or roles].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (223,'SECURITY ASSESSMENTS | INDEPENDENT ASSESSORS','The organization employs assessors or assessment teams with [Assignment: organization-defined level of independence] to conduct security control assessments.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (224,'SECURITY ASSESSMENTS | SPECIALIZED ASSESSMENTS','The organization includes as part of security control assessments, [Assignment: organization-defined frequency], [Selection: announced; unannounced], [Selection (one or more): in-depth monitoring; vulnerability scanning; malicious user testing; insider threat assessment; performance/load testing; [Assignment: organization-defined other forms of security assessment]].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (225,'SECURITY ASSESSMENTS | EXTERNAL ORGANIZATIONS','The organization accepts the results of an assessment of [Assignment: organization-defined information system] performed by [Assignment: organization-defined external organization] when the assessment meets [Assignment: organization-defined requirements].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (226,'SYSTEM INTERCONNECTIONS','The organization: a. Authorizes connections from the information system to other information systems through the use of Interconnection Security Agreements; b. Documents, for each interconnection, the interface characteristics, security requirements, and the nature of the information communicated; and c. Reviews and updates Interconnection Security Agreements [Assignment: organization-defined frequency].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (227,'SYSTEM INTERCONNECTIONS | UNCLASSIFIED NATIONAL SECURITY SYSTEM CONNECTIONS','The organization prohibits the direct connection of an [Assignment: organization-defined unclassified, national security system] to an external network without the use of [Assignment: organization-defined boundary protection device].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (228,'SYSTEM INTERCONNECTIONS | CLASSIFIED NATIONAL SECURITY SYSTEM CONNECTIONS','The organization prohibits the direct connection of a classified, national security system to an external network without the use of [Assignment: organization-defined boundary protection device].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (229,'SYSTEM INTERCONNECTIONS | UNCLASSIFIED NON-NATIONAL SECURITY SYSTEM CONNECTIONS','The organization prohibits the direct connection of an [Assignment: organization-defined unclassified, non-national security system] to an external network without the use of [Assignment; organization-defined boundary protection device].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (230,'SYSTEM INTERCONNECTIONS | CONNECTIONS TO PUBLIC NETWORKS','The organization prohibits the direct connection of an [Assignment: organization-defined information system] to a public network.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (231,'SYSTEM INTERCONNECTIONS | RESTRICTIONS ON EXTERNAL SYSTEM CONNECTIONS','The organization employs [Selection: allow-all, deny-by-exception; deny-all, permit-by-exception] policy for allowing [Assignment: organization-defined information systems] to connect to external information systems.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (232,'SYSTEM INTERCONNECTIONS','Withdrawn: Incorporated into CA-2.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (233,'PLAN OF ACTION AND MILESTONES','The organization: a. Develops a plan of action and milestones for the information system to document the organization’s planned remedial actions to correct weaknesses or deficiencies noted during the assessment of the security controls and to reduce or eliminate known vulnerabilities in the system; and b. Updates existing plan of action and milestones [Assignment: organization-defined frequency] based on the findings from security controls assessments, security impact analyses, and continuous monitoring activities.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (234,'PLAN OF ACTION AND MILESTONES | AUTOMATION SUPPORT FOR ACCURACY / CURRENCY','The organization employs automated mechanisms to help ensure that the plan of action and milestones for the information system is accurate, up to date, and readily available.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (235,'SECURITY AUTHORIZATION',' The organization: a. Assigns a senior-level executive or manager as the authorizing official for the information system; b. Ensures that the authorizing official authorizes the information system for processing before commencing operations; and c. Updates the security authorization [Assignment: organization-defined frequency].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (236,'CONTINUOUS MONITORING','The organization develops a continuous monitoring strategy and implements a continuous monitoring program that includes:\n			a. Establishment of [Assignment: organization-defined metrics] to be monitored;\n			b. Establishment of [Assignment: organization-defined frequencies] for monitoring and [Assignment: organization-defined frequencies] for assessments supporting such monitoring;\n			c. Ongoing security control assessments in accordance with the organizational continuous monitoring strategy;\n			d. Ongoing security status monitoring of organization-defined metrics in accordance with the organizational continuous monitoring strategy;\n			e. Correlation and analysis of security-related information generated by assessments and monitoring;\n			f. Response actions to address results of the analysis of security-related information; and\n			g. Reporting the security status of organization and the information system to [Assignment: organization-defined personnel or roles] [Assignment: organization-defined frequency].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (237,'CONTINUOUS MONITORING | INDEPENDENT ASSESSMENT','The organization employs assessors or assessment teams with [Assignment: organization-defined level of independence] to monitor the security controls in the information system on an ongoing basis.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (238,'CONTINUOUS MONITORING | TYPES OF ASSESSMENTS','Withdrawn: Incorporated into CA-2.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (239,'CONTINUOUS MONITORING | TREND ANALYSES','The organization employs trend analyses to determine if security control implementations, the frequency of continuous monitoring activities, and/or the types of activities used in the continuous monitoring process need to be modified based on empirical data.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (240,'PENETRATION TESTING',' \n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (241,'PENETRATION TESTING | INDEPENDENT PENETRATION AGENT OR TEAM','The organization employs an independent penetration agent or penetration team to perform penetration testing on the information system or system components.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (242,'PENETRATION TESTING | RED TEAM EXERCISES','The organization employs [Assignment: organization-defined red team exercises] to simulate attempts by adversaries to compromise organizational information systems in accordance with [Assignment: organization-defined rules of engagement].\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (243,'INTERNAL SYSTEM CONNECTIONS','The organization: a. Authorizes internal connections of [Assignment: organization-defined information system components or classes of components] to the information system; and b. Documents, for each internal connection, the interface characteristics, security requirements, and the nature of the information communicated.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (244,'INTERNAL SYSTEM CONNECTIONS | SECURITY COMPLIANCE CHECKS','The information system performs security compliance checks on constituent system components prior to the establishment of the internal connection.\n		',NULL,4);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (245,'CONFIGURATION MANAGEMENT POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A configuration management policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the configuration management policy and associated configuration management controls; and\n			b. Reviews and updates the current:\n				1. Configuration management policy [Assignment: organization-defined frequency]; and\n				2. Configuration management procedures [Assignment: organization-defined frequency].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (246,'BASELINE CONFIGURATION','The organization develops, documents, and maintains under configuration control, a current baseline configuration of the information system.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (247,'BASELINE CONFIGURATION | REVIEWS AND UPDATES','The organization reviews and updates the baseline configuration of the information system: (a) [Assignment: organization-defined frequency]; (b) When required due to [Assignment organization-defined circumstances]; and (c) As an integral part of information system component installations and upgrades.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (248,'BASELINE CONFIGURATION | AUTOMATION SUPPORT FOR ACCURACY / CURRENCY','The organization employs automated mechanisms to maintain an up-to-date, complete, accurate, and readily available baseline configuration of the information system.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (249,'BASELINE CONFIGURATION | RETENTION OF PREVIOUS CONFIGURATIONS','The organization retains [Assignment: organization-defined previous versions of baseline configurations of the information system] to support rollback.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (250,'BASELINE CONFIGURATION | UNAUTHORIZED SOFTWARE','Withdrawn: Incorporated into CM-7.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (251,'BASELINE CONFIGURATION | AUTHORIZED SOFTWARE','Withdrawn: Incorporated into CM-7.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (252,'BASELINE CONFIGURATION | DEVELOPMENT AND TEST ENVIRONMENTS','The organization maintains a baseline configuration for information system development and test environments that is managed separately from the operational baseline configuration.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (253,'BASELINE CONFIGURATION | CONFIGURE SYSTEMS, COMPONENTS, OR DEVICES FOR HIGH-RISK AREAS','The organization: (a) Issues [Assignment: organization-defined information systems, system components, or devices] with [Assignment: organization-defined configurations] to individuals traveling to locations that the organization deems to be of significant risk; and (b) Applies [Assignment: organization-defined security safeguards] to the devices when the individuals return.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (254,'CONFIGURATION CHANGE CONTROL','The organization:\n			a. Determines the types of changes to the information system that are configuration-controlled;\n			b. Reviews proposed configuration-controlled changes to the information system and approves or disapproves such changes with explicit consideration for security impact analyses;\n			c. Documents configuration change decisions associated with the information system;\n			d. Implements approved configuration-controlled changes to the information system;\n			e. Retains records of configuration-controlled changes to the information system for [Assignment: organization-defined time period];\n			f. Audits and reviews activities associated with configuration-controlled changes to the information system; and\n			g. Coordinates and provides oversight for configuration change control activities through [Assignment: organization-defined configuration change control element (e.g., committee, board)] that convenes [Selection (one or more): [Assignment: organization-defined frequency]; [Assignment: organization-defined configuration change conditions]].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (255,'CONFIGURATION CHANGE CONTROL | AUTOMATED DOCUMENT / NOTIFICATION / PROHIBITION OF CHANGES','The organization employs automated mechanisms to: (a) Document proposed changes to the information system; (b) Notify [Assignment: organized-defined approval authorities] of proposed changes to the information system and request change approval; (c) Highlight proposed changes to the information system that have not been approved or disapproved by [Assignment: organization-defined time period]; (d) Prohibit changes to the information system until designated approvals are received; (e) Document all changes to the information system; and (f) Notify [Assignment: organization-defined personnel] when approved changes to the information system are completed.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (256,'CONFIGURATION CHANGE CONTROL | TEST / VALIDATE / DOCUMENT CHANGES','The organization tests, validates, and documents changes to the information system before implementing the changes on the operational system.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (257,'CONFIGURATION CHANGE CONTROL | AUTOMATED CHANGE IMPLEMENTATION','The organization employs automated mechanisms to implement changes to the current information system baseline and deploys the updated baseline across the installed base.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (258,'CONFIGURATION CHANGE CONTROL | SECURITY REPRESENTATIVE','The organization requires an information security representative to be a member of the [Assignment: organization-defined configuration change control element].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (259,'CONFIGURATION CHANGE CONTROL | AUTOMATED SECURITY RESPONSE','The information system implements [Assignment: organization-defined security responses] automatically if baseline configurations are changed in an unauthorized manner.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (260,'CONFIGURATION CHANGE CONTROL | CRYPTOGRAPHY MANAGEMENT','The organization ensures that cryptographic mechanisms used to provide [Assignment: organization-defined security safeguards] are under configuration management.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (261,'SECURITY IMPACT ANALYSIS','The organization analyzes changes to the information system to determine potential security impacts prior to change implementation.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (262,'SECURITY IMPACT ANALYSIS | SEPARATE TEST ENVIRONMENTS','The organization analyzes changes to the information system in a separate test environment before implementation in an operational environment, looking for security impacts due to flaws, weaknesses, incompatibility, or intentional malice.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (263,'SECURITY IMPACT ANALYSIS | VERIFICATION OF SECURITY FUNCTIONS','The organization, after the information system is changed, checks the security functions to verify that the functions are implemented correctly, operating as intended, and producing the desired outcome with regard to meeting the security requirements for the system.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (264,'ACCESS RESTRICTIONS FOR CHANGE','The organization defines, documents, approves, and enforces physical and logical access restrictions associated with changes to the information system.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (265,'ACCESS RESTRICTIONS FOR CHANGE | AUTOMATED ACCESS ENFORCEMENT / AUDITING','The information system enforces access restrictions and supports auditing of the enforcement actions.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (266,'ACCESS RESTRICTIONS FOR CHANGE | REVIEW SYSTEM CHANGES','The organization reviews information system changes [Assignment: organization-defined frequency] and [Assignment: organization-defined circumstances] to determine whether unauthorized changes have occurred.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (267,'ACCESS RESTRICTIONS FOR CHANGE | SIGNED COMPONENTS','The information system prevents the installation of [Assignment: organization-defined software and firmware components] without verification that the component has been digitally signed using a certificate that is recognized and approved by the organization.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (268,'ACCESS RESTRICTIONS FOR CHANGE | DUAL AUTHORIZATION','The organization enforces dual authorization for implementing changes to [Assignment: organization-defined information system components and system-level information].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (269,'ACCESS RESTRICTIONS FOR CHANGE | LIMIT PRODUCTION / OPERATIONAL PRIVILEGES','The organization: (a) Limits privileges to change information system components and system-related information within a production or operational environment; and (b) Reviews and reevaluates privileges [Assignment: organization-defined frequency].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (270,'ACCESS RESTRICTIONS FOR CHANGE | LIMIT LIBRARY PRIVILEGES','The organization limits privileges to change software resident within software libraries.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (271,'ACCESS RESTRICTIONS FOR CHANGE | AUTOMATIC IMPLEMENTATION OF SECURITY SAFEGUARDS','Withdrawn: Incorporated into SI-7.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (272,'CONFIGURATION SETTINGS','The organization: a. Establishes and documents configuration settings for information technology products employed within the information system using [Assignment: organization-defined security configuration checklists] that reflect the most restrictive mode consistent with operational requirements; b. Implements the configuration settings; c. Identifies, documents, and approves any deviations from established configuration settings for [Assignment: organization-defined information system components] based on [Assignment: organization-defined operational requirements]; and d. Monitors and controls changes to the configuration settings in accordance with organizational policies and procedures.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (273,'CONFIGURATION SETTINGS | AUTOMATED CENTRAL MANAGEMENT / APPLICATION / VERIFICATION','The organization employs automated mechanisms to centrally manage, apply, and verify configuration settings for [Assignment: organization-defined information system components].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (274,'CONFIGURATION SETTINGS | RESPOND TO UNAUTHORIZED CHANGES','The organization employs [Assignment: organization-defined security safeguards] to respond to unauthorized changes to [Assignment: organization-defined configuration settings].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (275,'CONFIGURATION SETTINGS | UNAUTHORIZED CHANGE DETECTION','Withdrawn: Incorporated into SI-7.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (276,'CONFIGURATION SETTINGS | CONFORMANCE DEMONSTRATION','Withdrawn: Incorporated into CM-4.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (277,'LEAST FUNCTIONALITY','The organization: a. Configures the information system to provide only essential capabilities; and b. Prohibits or restricts the use of the following functions, ports, protocols, and/or services: [Assignment: organization-defined prohibited or restricted functions, ports, protocols, and/or services].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (278,'LEAST FUNCTIONALITY | PERIODIC REVIEW','The organization: (a) Reviews the information system [Assignment: organization-defined frequency] to identify unnecessary and/or nonsecure functions, ports, protocols, and services; and (b) Disables [Assignment: organization-defined functions, ports, protocols, and services within the information system deemed to be unnecessary and/or nonsecure].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (279,'LEAST FUNCTIONALITY | PREVENT PROGRAM EXECUTION','The information system prevents program execution in accordance with [Selection (one or more): [Assignment: organization-defined policies regarding software program usage and restrictions]; rules authorizing the terms and conditions of software program usage].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (280,'LEAST FUNCTIONALITY | REGISTRATION COMPLIANCE','The organization ensures compliance with [Assignment: organization-defined registration requirements for functions, ports, protocols, and services].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (281,'LEAST FUNCTIONALITY | UNAUTHORIZED SOFTWARE / BLACKLISTING','The organization: (a) Identifies [Assignment: organization-defined software programs not authorized to execute on the information system]; (b) Employs an allow-all, deny-by-exception policy to prohibit the execution of unauthorized software programs on the information system; and (c) Reviews and updates the list of unauthorized software programs [Assignment: organization-defined frequency].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (282,'LEAST FUNCTIONALITY | AUTHORIZED SOFTWARE / WHITELISTING','The organization: (a) Identifies [Assignment: organization-defined software programs authorized to execute on the information system]; (b) Employs a deny-all, permit-by-exception policy to allow the execution of authorized software programs on the information system; and (c) Reviews and updates the list of authorized software programs [Assignment: organization-defined frequency].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (283,'INFORMATION SYSTEM COMPONENT INVENTORY','The organization:\n			a. Develops and documents an inventory of information system components that:\n				1. Accurately reflects the current information system;\n				2. Includes all components within the authorization boundary of the information system;\n				3. Is at the level of granularity deemed necessary for tracking and reporting; and\n				4. Includes [Assignment: organization-defined information deemed necessary to achieve effective information system component accountability]; and\n			b. Reviews and updates the information system component inventory [Assignment: organization-defined frequency].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (284,'INFORMATION SYSTEM COMPONENT INVENTORY | UPDATES DURING INSTALLATIONS / REMOVALS','The organization updates the inventory of information system components as an integral part of component installations, removals, and information system updates.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (285,'INFORMATION SYSTEM COMPONENT INVENTORY | AUTOMATED MAINTENANCE','The organization employs automated mechanisms to help maintain an up-to-date, complete, accurate, and readily available inventory of information system components.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (286,'INFORMATION SYSTEM COMPONENT INVENTORY | AUTOMATED UNAUTHORIZED COMPONENT DETECTION','The organization:(a) Employs automated mechanisms [Assignment: organization-defined frequency] to detect the presence of unauthorized hardware, software, and firmware components within the information system; and (b) Takes the following actions when unauthorized components are detected: [Selection (one or more): disables network access by such components; isolates the components; notifies [Assignment: organization-defined personnel or roles]].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (287,'INFORMATION SYSTEM COMPONENT INVENTORY | ACCOUNTABILITY INFORMATION','The organization includes in the information system component inventory information, a means for identifying by [Selection (one or more): name; position; role], individuals responsible/accountable for administering those components.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (288,'INFORMATION SYSTEM COMPONENT INVENTORY | NO DUPLICATE ACCOUNTING OF COMPONENTS','The organization verifies that all components within the authorization boundary of the information system are not duplicated in other information system component inventories.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (289,'INFORMATION SYSTEM COMPONENT INVENTORY | ASSESSED CONFIGURATIONS / APPROVED DEVIATIONS','The organization includes assessed component configurations and any approved deviations to current deployed configurations in the information system component inventory.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (290,'INFORMATION SYSTEM COMPONENT INVENTORY | CENTRALIZED REPOSITORY','The organization provides a centralized repository for the inventory of information system components.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (291,'INFORMATION SYSTEM COMPONENT INVENTORY | AUTOMATED LOCATION TRACKING','The organization employs automated mechanisms to support tracking of information system components by geographic location.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (292,'INFORMATION SYSTEM COMPONENT INVENTORY | ASSIGNMENT OF COMPONENTS TO SYSTEMS','The organization: (a) Assigns [Assignment: organization-defined acquired information system components] to an information system; and (b) Receives an acknowledgement from the information system owner of this assignment.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (293,'CONFIGURATION MANAGEMENT PLAN','The organization develops, documents, and implements a configuration management plan for the information system that:\n			a. Addresses roles, responsibilities, and configuration management processes and procedures;\n			b. Establishes a process for identifying configuration items throughout the system development life cycle and for managing the configuration of the configuration items;\n			c. Defines the configuration items for the information system and places the configuration items under configuration management; and\n			d. Protects the configuration management plan from unauthorized disclosure and modification.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (294,'CONFIGURATION MANAGEMENT PLAN | ASSIGNMENT OF RESPONSIBILITY','The organization assigns responsibility for developing the configuration management process to organizational personnel that are not directly involved in information system development.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (295,'SOFTWARE USAGE RESTRICTIONS','The organization: a. Uses software and associated documentation in accordance with contract agreements and copyright laws; b. Tracks the use of software and associated documentation protected by quantity licenses to control copying and distribution; and c. Controls and documents the use of peer-to-peer file sharing technology to ensure that this capability is not used for the unauthorized distribution, display, performance, or reproduction of copyrighted work.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (296,'SOFTWARE USAGE RESTRICTIONS | OPEN SOURCE SOFTWARE','The organization establishes the following restrictions on the use of open source software: [Assignment: organization-defined restrictions].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (297,'USER-INSTALLED SOFTWARE','The organization:a. Establishes [Assignment: organization-defined policies] governing the installation of software by users; b. Enforces software installation policies through [Assignment: organization-defined methods]; andc. Monitors policy compliance at [Assignment: organization-defined frequency].\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (298,'USER-INSTALLED SOFTWARE | ALERTS FOR UNAUTHORIZED INSTALLATIONS','The information system alerts [Assignment: organization-defined personnel or roles] when the unauthorized installation of software is detected.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (299,'USER-INSTALLED SOFTWARE | PROHIBIT INSTALLATION WITHOUT PRIVILEGED STATUS','The information system prohibits user installation of software without explicit privileged status.\n		',NULL,5);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (300,'CONTINGENCY PLANNING POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A contingency planning policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the contingency planning policy and associated contingency planning controls; and\n			b. Reviews and updates the current:\n				1. Contingency planning policy [Assignment: organization-defined frequency]; and\n				2. Contingency planning procedures [Assignment: organization-defined frequency].\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (301,'CONTINGENCY PLAN','The organization:\n			a. Develops a contingency plan for the information system that:\n				1. Identifies essential missions and business functions and associated contingency requirements;\n				2. Provides recovery objectives, restoration priorities, and metrics;\n				3. Addresses contingency roles, responsibilities, assigned individuals with contact information;\n				4. Addresses maintaining essential missions and business functions despite an information system disruption, compromise, or failure;\n				5. Addresses eventual, full information system restoration without deterioration of the security safeguards originally planned and implemented; and\n				6. Is reviewed and approved by [Assignment: organization-defined personnel or roles];\n			b. Distributes copies of the contingency plan to [Assignment: organization-defined key contingency personnel (identified by name and/or by role) and organizational elements];\n			c. Coordinates contingency planning activities with incident handling activities;\n			d. Reviews the contingency plan for the information system [Assignment: organization-defined frequency];\n			e. Updates the contingency plan to address changes to the organization, information system, or environment of operation and problems encountered during contingency plan implementation, execution, or testing;\n			f. Communicates contingency plan changes to [Assignment: organization-defined key contingency personnel (identified by name and/or by role) and organizational elements]; and\n			g. Protects the contingency plan from unauthorized disclosure and modification.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (302,'CONTINGENCY PLAN | COORDINATE WITH RELATED PLANS','The organization coordinates contingency plan development with organizational elements responsible for related plans.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (303,'CONTINGENCY PLAN | CAPACITY PLANNING','The organization conducts capacity planning so that necessary capacity for information processing, telecommunications, and environmental support exists during contingency operations.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (304,'CONTINGENCY PLAN | RESUME ESSENTIAL MISSIONS / BUSINESS FUNCTIONS','The organization plans for the resumption of essential missions and business functions within [Assignment: organization-defined time period] of contingency plan activation.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (305,'CONTINGENCY PLAN | RESUME ALL MISSIONS / BUSINESS FUNCTIONS','The organization plans for the resumption of all missions and business functions within [Assignment: organization-defined time period] of contingency plan activation.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (306,'CONTINGENCY PLAN | CONTINUE ESSENTIAL MISSIONS / BUSINESS FUNCTIONS','The organization plans for the continuance of essential missions and business functions with little or no loss of operational continuity and sustains that continuity until full information system restoration at primary processing and/or storage sites.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (307,'CONTINGENCY PLAN | ALTERNATE PROCESSING / STORAGE SITE','The organization plans for the transfer of essential missions and business functions to alternate processing and/or storage sites with little or no loss of operational continuity and sustains that continuity through information system restoration to primary processing and/or storage sites.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (308,'CONTINGENCY PLAN | COORDINATE WITH EXTERNAL SERVICE PROVIDERS','The organization coordinates its contingency plan with the contingency plans of external service providers to ensure that contingency requirements can be satisfied.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (309,'CONTINGENCY PLAN | IDENTIFY CRITICAL ASSETS','The organization identifies critical information system assets supporting essential missions and business functions.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (310,'CONTINGENCY TRAINING','The organization provides contingency training to information system users consistent with assigned roles and responsibilities: a. Within [Assignment: organization-defined time period] of assuming a contingency role or responsibility; b. When required by information system changes; and c. [Assignment: organization-defined frequency] thereafter.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (311,'CONTINGENCY TRAINING | SIMULATED EVENTS','The organization incorporates simulated events into contingency training to facilitate effective response by personnel in crisis situations.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (312,'CONTINGENCY TRAINING | AUTOMATED TRAINING ENVIRONMENTS','The organization employs automated mechanisms to provide a more thorough and realistic contingency training environment.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (313,'CONTINGENCY PLAN TESTING','The organization: a. Tests the contingency plan for the information system [Assignment: organization-defined frequency] using [Assignment: organization-defined tests] to determine the effectiveness of the plan and the organizational readiness to execute the plan; b. Reviews the contingency plan test results; and c. Initiates corrective actions, if needed.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (314,'CONTINGENCY PLAN TESTING | COORDINATE WITH RELATED PLANS','The organization coordinates contingency plan testing with organizational elements responsible for related plans.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (315,'CONTINGENCY PLAN TESTING | ALTERNATE PROCESSING SITE','The organization tests the contingency plan at the alternate processing site: (a) To familiarize contingency personnel with the facility and available resources; and (b) To evaluate the capabilities of the alternate processing site to support contingency operations.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (316,'CONTINGENCY PLAN TESTING | AUTOMATED TESTING','The organization employs automated mechanisms to more thoroughly and effectively test the contingency plan.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (317,'CONTINGENCY PLAN TESTING | FULL RECOVERY / RECONSTITUTION','The organization includes a full recovery and reconstitution of the information system to a known state as part of contingency plan testing.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (318,'','Withdrawn: Incorporated into CP-2.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (319,'ALTERNATE STORAGE SITE','The organization: a. Establishes an alternate storage site including necessary agreements to permit the storage and retrieval of information system backup information; and b. Ensures that the alternate storage site provides information security safeguards equivalent to that of the primary site.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (320,'ALTERNATE STORAGE SITE | SEPARATION FROM PRIMARY SITE','The organization identifies an alternate storage site that is separated from the primary storage site to reduce susceptibility to the same threats.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (321,'ALTERNATE STORAGE SITE | RECOVERY TIME / POINT OBJECTIVES','The organization configures the alternate storage site to facilitate recovery operations in accordance with recovery time and recovery point objectives.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (322,'ALTERNATE STORAGE SITE | ACCESSIBILITY','The organization identifies potential accessibility problems to the alternate storage site in the event of an area-wide disruption or disaster and outlines explicit mitigation actions.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (323,'ALTERNATE PROCESSING SITE','The organization: a. Establishes an alternate processing site including necessary agreements to permit the transfer and resumption of [Assignment: organization-defined information system operations] for essential missions/business functions within [Assignment: organization-defined time periodconsistent with recovery time and recovery point objectives] when the primary processing capabilities are unavailable; b. Ensures that equipment and supplies required to transfer and resume operations are available at the alternate processing site or contracts are in place to support delivery to the site within the organization-defined time period for transfer/resumption; and c. Ensures that the alternate processing site provides information security safeguards equivalent to those of the primary site.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (324,'ALTERNATE PROCESSING SITE | SEPARATION FROM PRIMARY SITE','The organization identifies an alternate processing site that is separated from the primary processing site to reduce susceptibility to the same threats.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (325,'ALTERNATE PROCESSING SITE | ACCESSIBILITY','The organization identifies potential accessibility problems to the alternate processing site in the event of an area-wide disruption or disaster and outlines explicit mitigation actions.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (326,'ALTERNATE PROCESSING SITE | PRIORITY OF SERVICE','The organization develops alternate processing site agreements that contain priority-of-service provisions in accordance with organizational availability requirements (including recovery time objectives).\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (327,'ALTERNATE PROCESSING SITE | PREPARATION FOR USE','The organization prepares the alternate processing site so that the site is ready to be used as the operational site supporting essential missions and business functions.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (328,'ALTERNATE PROCESSING SITE | EQUIVALENT INFORMATION SECURITY SAFEGUARDS','Withdrawn: Incorporated into CP-7.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (329,'ALTERNATE PROCESSING SITE | INABILITY TO RETURN TO PRIMARY SITE','The organization plans and prepares for circumstances that preclude returning to the primary processing site.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (330,'TELECOMMUNICATIONS SERVICES','The organization establishes alternate telecommunications services including necessary agreements to permit the resumption of [Assignment: organization-defined information system operations] for essential missions and business functions within [Assignment: organization-defined time period] when the primary telecommunications capabilities are unavailable at either the primary or alternate processing or storage sites.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (331,'TELECOMMUNICATIONS SERVICES | PRIORITY OF SERVICE PROVISIONS','The organization:(a) Develops primary and alternate telecommunications service agreements that contain priority-of-service provisions in accordance with organizational availability requirements (including recovery time objectives); and (b) Requests Telecommunications Service Priority for all telecommunications services used for national security emergency preparedness in the event that the primary and/or alternate telecommunications services are provided by a common carrier.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (332,'TELECOMMUNICATIONS SERVICES | SINGLE POINTS OF FAILURE','The organization obtains alternate telecommunications services to reduce the likelihood of sharing a single point of failure with primary telecommunications services.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (333,'TELECOMMUNICATIONS SERVICES | SEPARATION OF PRIMARY / ALTERNATE PROVIDERS','The organization obtains alternate telecommunications services from providers that are separated from primary service providers to reduce susceptibility to the same threats.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (334,'TELECOMMUNICATIONS SERVICES | PROVIDER CONTINGENCY PLAN','The organization: (a) Requires primary and alternate telecommunications service providers to have contingency plans; (b) Reviews provider contingency plans to ensure that the plans meet organizational contingency requirements; and (c) Obtains evidence of contingency testing/training by providers [Assignment: organization-defined frequency].\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (335,'TELECOMMUNICATIONS SERVICES | ALTERNATE TELECOMMUNICATION SERVICE TESTING','The organization tests alternate telecommunication services [Assignment: organization-defined frequency].\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (336,'INFORMATION SYSTEM BACKUP','The organization: a. Conducts backups of user-level information contained in the information system [Assignment: organization-defined frequency consistent with recovery time and recovery point objectives]; b. Conducts backups of system-level information contained in the information system [Assignment: organization-defined frequency consistent with recovery time and recovery point objectives]; c. Conducts backups of information system documentation including security-related documentation [Assignment: organization-defined frequency consistent with recovery time and recovery point objectives]; and d. Protects the confidentiality, integrity, and availability of backup information at storage locations.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (337,'INFORMATION SYSTEM BACKUP | TESTING FOR RELIABILITY / INTEGRITY','The organization tests backup information [Assignment: organization-defined frequency] to verify media reliability and information integrity.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (338,'INFORMATION SYSTEM BACKUP | TEST RESTORATION USING SAMPLING','The organization uses a sample of backup information in the restoration of selected information system functions as part of contingency plan testing.\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (339,'INFORMATION SYSTEM BACKUP | SEPARATE STORAGE FOR CRITICAL INFORMATION','The organization stores backup copies of [Assignment: organization-defined critical information system software and other security-related information] in a separate facility or in a fire-rated container that is not collocated with the operational system.\n		',NULL,6);
