INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (341,'INFORMATION SYSTEM BACKUP | TRANSFER TO ALTERNATE STORAGE SITE','The organization transfers information system backup information to the alternate storage site [Assignment: organization-defined time period and transfer rate consistent with the recovery time and recovery point objectives].\n		',NULL,6);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (342,'INFORMATION SYSTEM BACKUP | REDUNDANT SECONDARY SYSTEM','The organization accomplishes information system backup by maintaining a redundant secondary system that is not collocated with the primary system and that can be activated without loss of information or disruption to operations.\n		',NULL,6);
