INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (370,'DEVICE IDENTIFICATION AND AUTHENTICATION | CRYPTOGRAPHIC BIDIRECTIONAL AUTHENTICATION','The information system authenticates [Assignment: organization-defined specific devices and/or types of devices] before establishing [Selection (one or more): local; remote; network] connection using bidirectional authentication that is cryptographically based.\n		',NULL,7);
