INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (372,'DEVICE IDENTIFICATION AND AUTHENTICATION | DYNAMIC ADDRESS ALLOCATION','The organization:(a) Standardizes dynamic address allocation lease information and the lease duration assigned to devices in accordance with [Assignment: organization-defined lease information and lease duration]; and (b) Audits lease information when assigned to a device.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (373,'DEVICE IDENTIFICATION AND AUTHENTICATION | DEVICE ATTESTATION','The organization ensures that device identification and authentication based on attestation is handled by [Assignment: organization-defined configuration management process].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (374,'IDENTIFIER MANAGEMENT','The organization manages information system identifiers by:\n			a. Receiving authorization from [Assignment: organization-defined personnel or roles] to assign an individual, group, role, or device identifier;\n			b. Selecting an identifier that identifies an individual, group, role, or device;\n			c. Assigning the identifier to the intended individual, group, role, or device;\n			d. Preventing reuse of identifiers for [Assignment: organization-defined time period]; and\n			e. Disabling the identifier after [Assignment: organization-defined time period of inactivity].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (375,'IDENTIFIER MANAGEMENT | PROHIBIT ACCOUNT IDENTIFIERS AS PUBLIC IDENTIFIERS','The organization prohibits the use of information system account identifiers that are the same as public identifiers for individual electronic mail accounts.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (376,'IDENTIFIER MANAGEMENT | SUPERVISOR AUTHORIZATION','The organization requires that the registration process to receive an individual identifier includes supervisor authorization.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (377,'IDENTIFIER MANAGEMENT | MULTIPLE FORMS OF CERTIFICATION','The organization requires multiple forms of certification of individual identification be presented to the registration authority.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (378,'IDENTIFIER MANAGEMENT | IDENTIFY USER STATUS','The organization manages individual identifiers by uniquely identifying each individual as [Assignment: organization-defined characteristic identifying individual status].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (379,'IDENTIFIER MANAGEMENT | DYNAMIC MANAGEMENT','The information system dynamically manages identifiers.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (380,'IDENTIFIER MANAGEMENT | CROSS-ORGANIZATION MANAGEMENT','The organization coordinates with [Assignment: organization-defined external organizations] for cross-organization management of identifiers.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (381,'IDENTIFIER MANAGEMENT | IN-PERSON REGISTRATION','The organization requires that the registration process to receive an individual identifier be conducted in person before a designated registration authority.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (382,'AUTHENTICATOR MANAGEMENT','The organization manages information system authenticators by:\n			a. Verifying, as part of the initial authenticator distribution, the identity of the individual, group, role, or device receiving the authenticator;\n			b. Establishing initial authenticator content for authenticators defined by the organization;\n			c. Ensuring that authenticators have sufficient strength of mechanism for their intended use;\n			d. Establishing and implementing administrative procedures for initial authenticator distribution, for lost/compromised or damaged authenticators, and for revoking authenticators;\n			e. Changing default content of authenticators prior to information system installation;\n			f. Establishing minimum and maximum lifetime restrictions and reuse conditions for authenticators;\n			g. Changing/refreshing authenticators [Assignment: organization-defined time period by authenticator type];\n			h. Protecting authenticator content from unauthorized disclosure and modification;\n			i. Requiring individuals to take, and having devices implement, specific security safeguards to protect authenticators; and\n			j. Changing authenticators for group/role accounts when membership to those accounts changes.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (383,'AUTHENTICATOR MANAGEMENT | PASSWORD-BASED AUTHENTICATION','The information system, for password-based authentication:\n			(a) Enforces minimum password complexity of [Assignment: organization-defined requirements for case sensitivity, number of characters, mix of upper-case letters, lower-case letters, numbers, and special characters, including minimum requirements for each type];\n			(b) Enforces at least the following number of changed characters when new passwords are created: [Assignment: organization-defined number];\n			(c) Stores and transmits only cryptographically-protected passwords;\n			(d) Enforces password minimum and maximum lifetime restrictions of [Assignment: organization-defined numbers for lifetime minimum, lifetime maximum];\n			(e) Prohibits password reuse for [Assignment: organization-defined number] generations; and\n			(f) Allows the use of a temporary password for system logons with an immediate change to a permanent password.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (384,'AUTHENTICATOR MANAGEMENT | PKI-BASED AUTHENTICATION','The information system, for PKI-based authentication: (a) Validates certifications by constructing and verifying a certification path to an accepted trust anchor including checking certificate status information; (b) Enforces authorized access to the corresponding private key; (c) Maps the authenticated identity to the account of the individual or group; and (d) Implements a local cache of revocation data to support path discovery and validation in case of inability to access revocation information via the network.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (385,'AUTHENTICATOR MANAGEMENT | IN-PERSON OR TRUSTED THIRD-PARTY REGISTRATION','The organization requires that the registration process to receive [Assignment: organization-defined types of and/or specific authenticators] be conducted [Selection: in person; by a trusted third party] before [Assignment: organization-defined registration authority] with authorization by [Assignment: organization-defined personnel or roles].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (386,'AUTHENTICATOR MANAGEMENT | AUTOMATED SUPPORT FOR PASSWORD STRENGTH DETERMINATION','The organization employs automated tools to determine if password authenticators are sufficiently strong to satisfy [Assignment: organization-defined requirements].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (387,'AUTHENTICATOR MANAGEMENT | CHANGE AUTHENTICATORS PRIOR TO DELIVERY','The organization requires developers/installers of information system components to provide unique authenticators or change default authenticators prior to delivery/installation.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (388,'AUTHENTICATOR MANAGEMENT | PROTECTION OF AUTHENTICATORS','The organization protects authenticators commensurate with the security category of the information to which use of the authenticator permits access.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (389,'AUTHENTICATOR MANAGEMENT | NO EMBEDDED UNENCRYPTED STATIC AUTHENTICATORS','The organization ensures that unencrypted static authenticators are not embedded in applications or access scripts or stored on function keys.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (390,'AUTHENTICATOR MANAGEMENT | MULTIPLE INFORMATION SYSTEM ACCOUNTS','The organization implements [Assignment: organization-defined security safeguards] to manage the risk of compromise due to individuals having accounts on multiple information systems.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (391,'AUTHENTICATOR MANAGEMENT | CROSS-ORGANIZATION CREDENTIAL MANAGEMENT','The organization coordinates with [Assignment: organization-defined external organizations] for cross-organization management of credentials.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (392,'AUTHENTICATOR MANAGEMENT | DYNAMIC CREDENTIAL ASSOCIATION','The information system dynamically provisions identities.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (393,'AUTHENTICATOR MANAGEMENT | HARDWARE TOKEN-BASED AUTHENTICATION','The information system, for hardware token-based authentication, employs mechanisms that satisfy [Assignment: organization-defined token quality requirements].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (394,'AUTHENTICATOR MANAGEMENT | BIOMETRIC-BASED AUTHENTICATION','The information system, for biometric-based authentication, employs mechanisms that satisfy [Assignment: organization-defined biometric quality requirements].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (395,'AUTHENTICATOR MANAGEMENT | EXPIRATION OF CACHED AUTHENTICATORS','The information system prohibits the use of cached authenticators after [Assignment: organization-defined time period].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (396,'AUTHENTICATOR MANAGEMENT | MANAGING CONTENT OF PKI TRUST STORES','The organization, for PKI-based authentication, employs a deliberate organization-wide methodology for managing the content of PKI trust stores installed across all platforms including networks, operating systems, browsers, and applications.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (397,'AUTHENTICATOR MANAGEMENT | FICAM-APPROVED PRODUCTS AND SERVICES','The organization uses only FICAM-approved path discovery and validation products and services.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (398,'AUTHENTICATOR FEEDBACK','The information system obscures feedback of authentication information during the authentication process to protect the information from possible exploitation/use by unauthorized individuals.\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (399,'CRYPTOGRAPHIC MODULE AUTHENTICATION','The information system implements mechanisms for authentication to a cryptographic module that meet the requirements of applicable federal laws, Executive Orders, directives, policies, regulations, standards, and guidance for such authentication.\n		',NULL,7);
