INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (409,'ADAPTIVE IDENTIFICATION AND AUTHENTICATION','The organization requires that individuals accessing the information system employ [Assignment: organization-defined supplemental authentication techniques or mechanisms] under specific [Assignment: organization-defined circumstances or situations].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (410,'RE-AUTHENTICATION','The organization requires users and devices to re-authenticate when [Assignment: organization-defined circumstances or situations requiring re-authentication].\n		',NULL,7);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (411,'INCIDENT RESPONSE POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. An incident response policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the incident response policy and associated incident response controls; and\n			b. Reviews and updates the current:\n				1. Incident response policy [Assignment: organization-defined frequency]; and\n				2. Incident response procedures [Assignment: organization-defined frequency].\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (412,'INCIDENT RESPONSE TRAINING','The organization provides incident response training to information system users consistent with assigned roles and responsibilities: a. Within [Assignment: organization-defined time period] of assuming an incident response role or responsibility; b. When required by information system changes; and c. [Assignment: organization-defined frequency] thereafter.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (413,'INCIDENT RESPONSE TRAINING | SIMULATED EVENTS','The organization incorporates simulated events into incident response training to facilitate effective response by personnel in crisis situations.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (414,'INCIDENT RESPONSE TRAINING | AUTOMATED TRAINING ENVIRONMENTS','The organization employs automated mechanisms to provide a more thorough and realistic incident response training environment.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (415,'INCIDENT RESPONSE TESTING','The organization tests the incident response capability for the information system [Assignment: organization-defined frequency] using [Assignment: organization-defined tests] to determine the incident response effectiveness and documents the results.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (416,'INCIDENT RESPONSE TESTING | AUTOMATED TESTING','The organization employs automated mechanisms to more thoroughly and effectively test the incident response capability.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (417,'INCIDENT RESPONSE TESTING | COORDINATION WITH RELATED PLANS','The organization coordinates incident response testing with organizational elements responsible for related plans.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (418,'INCIDENT HANDLING','The organization: a. Implements an incident handling capability for security incidents that includes preparation, detection and analysis, containment, eradication, and recovery; b. Coordinates incident handling activities with contingency planning activities; and c. Incorporates lessons learned from ongoing incident handling activities into incident response procedures, training, and testing, and implements the resulting changes accordingly.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (419,'INCIDENT HANDLING | AUTOMATED INCIDENT HANDLING PROCESSES','The organization employs automated mechanisms to support the incident handling process.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (420,'INCIDENT HANDLING | DYNAMIC RECONFIGURATION','The organization includes dynamic reconfiguration of [Assignment: organization-defined information system components] as part of the incident response capability.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (421,'INCIDENT HANDLING | CONTINUITY OF OPERATIONS','The organization identifies [Assignment: organization-defined classes of incidents] and [Assignment: organization-defined actions to take in response to classes of incidents] to ensure continuation of organizational missions and business functions.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (422,'INCIDENT HANDLING | INFORMATION CORRELATION','The organization correlates incident information and individual incident responses to achieve an organization-wide perspective on incident awareness and response.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (423,'INCIDENT HANDLING | AUTOMATIC DISABLING OF INFORMATION SYSTEM','The organization implements a configurable capability to automatically disable the information system if [Assignment: organization-defined security violations] are detected.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (424,'INCIDENT HANDLING | INSIDER THREATS - SPECIFIC CAPABILITIES','The organization implements incident handling capability for insider threats.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (425,'INCIDENT HANDLING | INSIDER THREATS - INTRA-ORGANIZATION COORDINATION','The organization coordinates incident handling capability for insider threats across [Assignment: organization-defined components or elements of the organization].\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (426,'INCIDENT HANDLING | CORRELATION WITH EXTERNAL ORGANIZATIONS','The organization coordinates with [Assignment: organization-defined external organizations] to correlate and share [Assignment: organization-defined incident information] to achieve a cross-organization perspective on incident awareness and more effective incident responses.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (427,'INCIDENT HANDLING | DYNAMIC RESPONSE CAPABILITY','The organization employs [Assignment: organization-defined dynamic response capabilities] to effectively respond to security incidents.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (428,'INCIDENT HANDLING | SUPPLY CHAIN COORDINATION','The organization coordinates incident handling activities involving supply chain events with other organizations involved in the supply chain.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (429,'INCIDENTMONITORING','\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (430,'INCIDENT MONITORING | AUTOMATED TRACKING / DATA COLLECTION / ANALYSIS','The organization employs automated mechanisms to assist in the tracking of security incidents and in the collection and analysis of incident information.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (431,'INCIDENT REPORTING','The organization: a. Requires personnel to report suspected security incidents to the organizational incident response capability within [Assignment: organization-defined time period]; and b. Reports security incident information to [Assignment: organization-defined authorities].\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (432,'INCIDENT REPORTING | AUTOMATED REPORTING','The organization employs automated mechanisms to assist in the reporting of security incidents.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (433,'INCIDENT REPORTING | VULNERABILITIES RELATED TO INCIDENTS','The organization reports information system vulnerabilities associated with reported security incidents to [Assignment: organization-defined personnel or roles].\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (434,'INCIDENT REPORTING | COORDINATION WITH SUPPLY CHAIN','The organization provides security incident information to other organizations involved in the supply chain for information systems or information system components related to the incident.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (435,'INCIDENT RESPONSE ASSISTANCE','The organization provides an incident response support resource, integral to the organizational incident response capability that offers advice and assistance to users of the information system for the handling and reporting of security incidents.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (436,'INCIDENT RESPONSE ASSISTANCE | AUTOMATION SUPPORT FOR AVAILABILITY OF INFORMATION / SUPPORT','The organization employs automated mechanisms to increase the availability of incident response-related information and support.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (437,'INCIDENT RESPONSE ASSISTANCE | COORDINATION WITH EXTERNAL PROVIDERS','The organization:(a) Establishes a direct, cooperative relationship between its incident response capability and external providers of information system protection capability; and (b) Identifies organizational incident response team members to the external providers.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (438,'INCIDENT RESPONSE PLAN','The organization:\n			a. Develops an incident response plan that:\n				1. Provides the organization with a roadmap for implementing its incident response capability;\n				2. Describes the structure and organization of the incident response capability;\n				3. Provides a high-level approach for how the incident response capability fits into the overall organization;\n				4. Meets the unique requirements of the organization, which relate to mission, size, structure, and functions;\n				5. Defines reportable incidents;\n				6. Provides metrics for measuring the incident response capability within the organization;\n				7. Defines the resources and management support needed to effectively maintain and mature an incident response capability; and\n				8. Is reviewed and approved by [Assignment: organization-defined personnel or roles];\n			b. Distributes copies of the incident response plan to [Assignment: organization-defined incident response personnel (identified by name and/or by role) and organizational elements];\n			c. Reviews the incident response plan [Assignment: organization-defined frequency];\n			d. Updates the incident response plan to address system/organizational changes or problems encountered during plan implementation, execution, or testing;\n			e. Communicates incident response plan changes to [Assignment: organization-defined incident response personnel (identified by name and/or by role) and organizational elements]; and\n			f. Protects the incident response plan from unauthorized disclosure and modification.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (439,'INFORMATION SPILLAGE RESPONSE','The organization responds to information spills by:\n			a. Identifying the specific information involved in the information system contamination;\n			b. Alerting [Assignment: organization-defined personnel or roles] of the information spill using a method of communication not associated with the spill;\n			c. Isolating the contaminated information system or system component;\n			d. Eradicating the information from the contaminated information system or component;\n			e. Identifying other information systems or system components that may have been subsequently contaminated; and\n			f. Performing other [Assignment: organization-defined actions].\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (440,'INFORMATION SPILLAGE RESPONSE | RESPONSIBLE PERSONNEL','The organization assigns [Assignment: organization-defined personnel or roles] with responsibility for responding to information spills.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (441,'INFORMATION SPILLAGE RESPONSE | TRAINING','The organization provides information spillage response training [Assignment: organization-defined frequency].\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (442,'INFORMATION SPILLAGE RESPONSE | POST-SPILL OPERATIONS','The organization implements [Assignment: organization-defined procedures] to ensure that organizational personnel impacted by information spills can continue to carry out assigned tasks while contaminated systems are undergoing corrective actions.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (443,'INFORMATION SPILLAGE RESPONSE | EXPOSURE TO UNAUTHORIZED PERSONNEL','The organization employs [Assignment: organization-defined security safeguards] for personnel exposed to information not within assigned access authorizations.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (444,'INTEGRATED INFORMATION SECURITY ANALYSIS TEAM','The organization establishes an integrated team of forensic/malicious code analysts, tool developers, and real-time operations personnel.\n		',NULL,8);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (445,'SYSTEM MAINTENANCE POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A system maintenance policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the system maintenance policy and associated system maintenance controls; and\n			b. Reviews and updates the current:\n				1. System maintenance policy [Assignment: organization-defined frequency]; and\n				2. System maintenance procedures [Assignment: organization-defined frequency].\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (446,'CONTROLLED MAINTENANCE','The organization:\n			a. Schedules, performs, documents, and reviews records of maintenance and repairs on information system components in accordance with manufacturer or vendor specifications and/or organizational requirements;\n			b. Approves and monitors all maintenance activities, whether performed on site or remotely and whether the equipment is serviced on site or removed to another location;\n			c. Requires that [Assignment: organization-defined personnel or roles] explicitly approve the removal of the information system or system components from organizational facilities for off-site maintenance or repairs;\n			d. Sanitizes equipment to remove all information from associated media prior to removal from organizational facilities for off-site maintenance or repairs;\n			e. Checks all potentially impacted security controls to verify that the controls are still functioning properly following maintenance or repair actions; and\n			f. Includes [Assignment: organization-defined maintenance-related information] in organizational maintenance records.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (447,'CONTROLLED MAINTENANCE | RECORD CONTENT','Withdrawn: Incorporated into MA-2.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (448,'CONTROLLED MAINTENANCE | AUTOMATED MAINTENANCE ACTIVITIES','The organization: (a) Employs automated mechanisms to schedule, conduct, and document maintenance and repairs; and (b) Produces up-to date, accurate, and complete records of all maintenance and repair actions requested, scheduled, in process, and completed.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (449,'MAINTENANCE TOOLS','The organization approves, controls, and monitors information system maintenance tools.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (450,'MAINTENANCE TOOLS | INSPECT TOOLS','The organization inspects the maintenance tools carried into a facility by maintenance personnel for improper or unauthorized modifications.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (451,'MAINTENANCE TOOLS | INSPECT MEDIA','The organization checks media containing diagnostic and test programs for malicious code before the media are used in the information system.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (452,'MAINTENANCE TOOLS | PREVENT UNAUTHORIZED REMOVAL','The organization prevents the unauthorized removal of maintenance equipment containing organizational information by: (a) Verifying that there is no organizational information contained on the equipment; (b) Sanitizing or destroying the equipment; (c) Retaining the equipment within the facility; or (d) Obtaining an exemption from [Assignment: organization-defined personnel or roles] explicitly authorizing removal of the equipment from the facility.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (453,'MAINTENANCE TOOLS | RESTRICTED TOOL USE','The information system restricts the use of maintenance tools to authorized personnel only.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (454,'NONLOCAL MAINTENANCE','The organization: a. Approves and monitors nonlocal maintenance and diagnostic activities; b. Allows the use of nonlocal maintenance and diagnostic tools only as consistent with organizational policy and documented in the security plan for the information system; c. Employs strong authenticators in the establishment of nonlocal maintenance and diagnostic sessions; d. Maintains records for nonlocal maintenance and diagnostic activities; and e. Terminates session and network connections when nonlocal maintenance is completed.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (455,'NONLOCAL MAINTENANCE | AUDITING AND REVIEW','The organization: (a) Audits nonlocal maintenance and diagnostic sessions [Assignment: organization-defined audit events]; and (b) Reviews the records of the maintenance and diagnostic sessions.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (456,'NONLOCAL MAINTENANCE | DOCUMENT NONLOCAL MAINTENANCE','The organization documents in the security plan for the information system, the policies and procedures for the establishment and use of nonlocal maintenance and diagnostic connections.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (457,'NONLOCAL MAINTENANCE | COMPARABLE SECURITY / SANITIZATION','The organization:\n			(a) Requires that nonlocal maintenance and diagnostic services be performed from an information system that implements a security capability comparable to the capability implemented on the system being serviced; or\n			(b) Removes the component to be serviced from the information system prior to nonlocal maintenance or diagnostic services, sanitizes the component (with regard to organizational information) before removal from organizational facilities, and after the service is performed, inspects and sanitizes the component (with regard to potentially malicious software) before reconnecting the component to the information system.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (458,'NONLOCAL MAINTENANCE | AUTHENTICATION / SEPARATION OF MAINTENANCE SESSIONS','The organization protects nonlocal maintenance sessions by:\n			(a) Employing [Assignment: organization-defined authenticators that are replay resistant]; and\n			(b) Separating the maintenance sessions from other network sessions with the information system by either:\n				(1) Physically separated communications paths; or\n				(2) Logically separated communications paths based upon encryption.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (459,'NONLOCAL MAINTENANCE | APPROVALS AND NOTIFICATIONS','The organization: (a) Requires the approval of each nonlocal maintenance session by [Assignment: organization-defined personnel or roles]; and (b) Notifies [Assignment: organization-defined personnel or roles] of the date and time of planned nonlocal maintenance.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (460,'NONLOCAL MAINTENANCE | CRYPTOGRAPHIC PROTECTION','The information system implements cryptographic mechanisms to protect the integrity and confidentiality of nonlocal maintenance and diagnostic communications.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (461,'NONLOCAL MAINTENANCE | REMOTE DISCONNECT VERIFICATION','The information system implements remote disconnect verification at the termination of nonlocal maintenance and diagnostic sessions.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (462,'MAINTENANCE PERSONNEL','The organization: a. Establishes a process for maintenance personnel authorization and maintains a list of authorized maintenance organizations or personnel; b. Ensures that non-escorted personnel performing maintenance on the information system have required access authorizations; and c. Designates organizational personnel with required access authorizations and technical competence to supervise the maintenance activities of personnel who do not possess the required access authorizations.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (463,'MAINTENANCE PERSONNEL | INDIVIDUALS WITHOUT APPROPRIATE ACCESS','The organization:\n			(a) Implements procedures for the use of maintenance personnel that lack appropriate security clearances or are not U.S. citizens, that include the following requirements:\n				(1) Maintenance personnel who do not have needed access authorizations, clearances, or formal access approvals are escorted and supervised during the performance of maintenance and diagnostic activities on the information system by approved organizational personnel who are fully cleared, have appropriate access authorizations, and are technically qualified;\n				(2) Prior to initiating maintenance or diagnostic activities by personnel who do not have needed access authorizations, clearances or formal access approvals, all volatile information storage components within the information system are sanitized and all nonvolatile storage media are removed or physically disconnected from the system and secured; and\n			(b) Develops and implements alternate security safeguards in the event an information system component cannot be sanitized, removed, or disconnected from the system.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (464,'MAINTENANCE PERSONNEL | SECURITY CLEARANCES FOR CLASSIFIED SYSTEMS','The organization ensures that personnel performing maintenance and diagnostic activities on an information system processing, storing, or transmitting classified information possess security clearances and formal access approvals for at least the highest classification level and for all compartments of information on the system.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (465,'MAINTENANCE PERSONNEL | CITIZENSHIP REQUIREMENTS FOR CLASSIFIED SYSTEMS','The organization ensures that personnel performing maintenance and diagnostic activities on an information system processing, storing, or transmitting classified information are U.S. citizens.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (466,'MAINTENANCE PERSONNEL | FOREIGN NATIONALS','The organization ensures that:\n			(a) Cleared foreign nationals (i.e., foreign nationals with appropriate security clearances), are used to conduct maintenance and diagnostic activities on classified information systems only when the systems are jointly owned and operated by the United States and foreign allied governments, or owned and operated solely by foreign allied governments; and\n			(b) Approvals, consents, and detailed operational conditions regarding the use of foreign nationals to conduct maintenance and diagnostic activities on classified information systems are fully documented within Memoranda of Agreements.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (467,'MAINTENANCE PERSONNEL | NONSYSTEM-RELATED MAINTENANCE','The organization ensures that non-escorted personnel performing maintenance activities not directly associated with the information system but in the physical proximity of the system, have required access authorizations.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (468,'TIMELY MAINTENANCE','The organization obtains maintenance support and/or spare parts for [Assignment: organization-defined information system components] within [Assignment: organization-defined time period] of failure.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (469,'TIMELY MAINTENANCE | PREVENTIVE MAINTENANCE','The organization performs preventive maintenance on [Assignment: organization-defined information system components] at [Assignment: organization-defined time intervals].\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (470,'TIMELY MAINTENANCE | PREDICTIVE MAINTENANCE','The organization performs predictive maintenance on [Assignment: organization-defined information system components] at [Assignment: organization-defined time intervals].\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (471,'TIMELY MAINTENANCE | AUTOMATED SUPPORT FOR PREDICTIVE MAINTENANCE','The organization employs automated mechanisms to transfer predictive maintenance data to a computerized maintenance management system.\n		',NULL,9);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (472,'MEDIA PROTECTION POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A media protection policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the media protection policy and associated media protection controls; and\n			b. Reviews and updates the current:\n				1. Media protection policy [Assignment: organization-defined frequency]; and\n				2. Media protection procedures [Assignment: organization-defined frequency].\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (473,'MEDIA ACCESS','The organization restricts access to [Assignment: organization-defined types of digital and/or non-digital media] to [Assignment: organization-defined personnel or roles].\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (474,'MEDIA ACCESS | AUTOMATED RESTRICTED ACCESS','Withdrawn: Incorporated into MP-4 (2).\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (475,'MEDIA ACCESS | CRYPTOGRAPHIC PROTECTION','Withdrawn: Incorporated into SC-28 (1).\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (476,'MEDIA MARKING','The organization: a. Marks information system media indicating the distribution limitations, handling caveats, and applicable security markings (if any) of the information; and b. Exempts [Assignment: organization-defined types of information system media] from marking as long as the media remain within [Assignment: organization-defined controlled areas].\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (477,'MEDIA STORAGE','The organization: a. Physically controls and securely stores [Assignment: organization-defined types of digital and/or non-digital media] within [Assignment: organization-defined controlled areas]; and b. Protects information system media until the media are destroyed or sanitized using approved equipment, techniques, and procedures.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (478,'MEDIA STORAGE | CRYPTOGRAPHIC PROTECTION','Withdrawn: Incorporated into SC-28 (1).\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (479,'MEDIA STORAGE | AUTOMATED RESTRICTED ACCESS','The organization employs automated mechanisms to restrict access to media storage areas and to audit access attempts and access granted.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (480,'MEDIA TRANSPORT','The organization: a. Protects and controls [Assignment: organization-defined types of information system media] during transport outside of controlled areas using [Assignment: organization-defined security safeguards]; b. Maintains accountability for information system media during transport outside of controlled areas; c. Documents activities associated with the transport of information system media; and d. Restricts the activities associated with the transport of information system media to authorized personnel.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (481,'MEDIA TRANSPORT | PROTECTION OUTSIDE OF CONTROLLED AREAS','Withdrawn: Incorporated into MP-5.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (482,'MEDIA TRANSPORT | DOCUMENTATION OF ACTIVITIES','Withdrawn: Incorporated into MP-5.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (483,'MEDIA TRANSPORT | CUSTODIANS','The organization employs an identified custodian during transport of information system media outside of controlled areas.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (484,'MEDIA TRANSPORT | CRYPTOGRAPHIC PROTECTION','The information system implements cryptographic mechanisms to protect the confidentiality and integrity of information stored on digital media during transport outside of controlled areas.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (485,'MEDIA SANITIZATION','The organization: a. Sanitizes [Assignment: organization-defined information system media] prior to disposal, release out of organizational control, or release for reuse using [Assignment: organization-defined sanitization techniques and procedures] in accordance with applicable federal and organizational standards and policies; and b. Employs sanitization mechanisms with the strength and integrity commensurate with the security category or classification of the information.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (486,'MEDIA SANITIZATION | REVIEW / APPROVE / TRACK / DOCUMENT / VERIFY','The organization reviews, approves, tracks, documents, and verifies media sanitization and disposal actions.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (487,'MEDIA SANITIZATION | EQUIPMENT TESTING','The organization tests sanitization equipment and procedures [Assignment: organization-defined frequency] to verify that the intended sanitization is being achieved.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (488,'MEDIA SANITIZATION | NONDESTRUCTIVE TECHNIQUES','The organization applies nondestructive sanitization techniques to portable storage devices prior to connecting such devices to the information system under the following circumstances: [Assignment: organization-defined circumstances requiring sanitization of portable storage devices].\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (489,'MEDIA SANITIZATION | CONTROLLED UNCLASSIFIED INFORMATION','Withdrawn: Incorporated into MP-6.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (490,'MEDIA SANITIZATION | CLASSIFIED INFORMATION','Withdrawn: Incorporated into MP-6.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (491,'MEDIA SANITIZATION | MEDIA DESTRUCTION','Withdrawn: Incorporated into MP-6.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (492,'MEDIA SANITIZATION | DUAL AUTHORIZATION','The organization enforces dual authorization for the sanitization of [Assignment: organization-defined information system media].\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (493,'MEDIA SANITIZATION | REMOTE PURGING / WIPING OF INFORMATION','The organization provides the capability to purge/wipe information from [Assignment: organization-defined information systems, system components, or devices] either remotely or under the following conditions: [Assignment: organization-defined conditions].\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (494,'MEDIA USE','The organization [Selection: restricts; prohibits] the use of [Assignment: organization-defined types of information system media] on [Assignment: organization-defined information systems or system components] using [Assignment: organization-defined security safeguards].\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (495,'MEDIA USE | PROHIBIT USE WITHOUT OWNER','The organization prohibits the use of portable storage devices in organizational information systems when such devices have no identifiable owner.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (496,'MEDIA USE | PROHIBIT USE OF SANITIZATION-RESISTANT MEDIA','The organization prohibits the use of sanitization-resistant media in organizational information systems.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (497,'MEDIA DOWNGRADING','The organization:\n			a. Establishes [Assignment: organization-defined information system media downgrading process] that includes employing downgrading mechanisms with [Assignment: organization-defined strength and integrity];\n			b. Ensures that the information system media downgrading process is commensurate with the security category and/or classification level of the information to be removed and the access authorizations of the potential recipients of the downgraded information;\n			c. Identifies [Assignment: organization-defined information system media requiring downgrading]; and\n			d. Downgrades the identified information system media using the established process.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (498,'MEDIA DOWNGRADING | DOCUMENTATION OF PROCESS','The organization documents information system media downgrading actions.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (499,'MEDIA DOWNGRADING | EQUIPMENT TESTING','The organization employs [Assignment: organization-defined tests] of downgrading equipment and procedures to verify correct performance [Assignment: organization-defined frequency].\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (500,'MEDIA DOWNGRADING | CONTROLLED UNCLASSIFIED INFORMATION','The organization downgrades information system media containing [Assignment: organization-defined Controlled Unclassified Information (CUI)] prior to public release in accordance with applicable federal and organizational standards and policies.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (501,'MEDIA DOWNGRADING | CLASSIFIED INFORMATION','The organization downgrades information system media containing classified information prior to release to individuals without required access authorizations in accordance with NSA standards and policies.\n		',NULL,10);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (502,'PHYSICAL AND ENVIRONMENTAL PROTECTION POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A physical and environmental protection policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the physical and environmental protection policy and associated physical and environmental protection controls; and\n			b. Reviews and updates the current:\n				1. Physical and environmental protection policy [Assignment: organization-defined frequency]; and\n				2. Physical and environmental protection procedures [Assignment: organization-defined frequency].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (503,'PHYSICAL ACCESS AUTHORIZATIONS','The organization: a. Develops, approves, and maintains a list of individuals with authorized access to the facility where the information system resides; b. Issues authorization credentials for facility access; c. Reviews the access list detailing authorized facility access by individuals [Assignment: organization-defined frequency]; and d. Removes individuals from the facility access list when access is no longer required.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (504,'PHYSICAL ACCESS AUTHORIZATIONS | ACCESS BY POSITION / ROLE','The organization authorizes physical access to the facility where the information system resides based on position or role.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (505,'PHYSICAL ACCESS AUTHORIZATIONS | TWO FORMS OF IDENTIFICATION','The organization requires two forms of identification from [Assignment: organization-defined list of acceptable forms of identification] for visitor access to the facility where the information system resides.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (506,'PHYSICAL ACCESS AUTHORIZATIONS | RESTRICT UNESCORTED ACCESS','The organization restricts unescorted access to the facility where the information system resides to personnel with [Selection (one or more): security clearances for all information contained within the system; formal access authorizations for all information contained within the system; need for access to all information contained within the system; [Assignment: organization-defined credentials]].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (507,'PHYSICAL ACCESS CONTROL','The organization:\n			a. Enforces physical access authorizations at [Assignment: organization-defined entry/exit points to the facility where the information system resides] by;\n				1. Verifying individual access authorizations before granting access to the facility; and\n				2. Controlling ingress/egress to the facility using [Selection (one or more): [Assignment: organization-defined physical access control systems/devices]; guards];\n			b. Maintains physical access audit logs for [Assignment: organization-defined entry/exit points];\n			c. Provides [Assignment: organization-defined security safeguards] to control access to areas within the facility officially designated as publicly accessible;\n			d. Escorts visitors and monitors visitor activity [Assignment: organization-defined circumstances requiring visitor escorts and monitoring];\n			e. Secures keys, combinations, and other physical access devices;\n			f. Inventories [Assignment: organization-defined physical access devices] every [Assignment: organization-defined frequency]; and\n			g. Changes combinations and keys [Assignment: organization-defined frequency] and/or when keys are lost, combinations are compromised, or individuals are transferred or terminated.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (508,'PHYSICAL ACCESS CONTROL | INFORMATION SYSTEM ACCESS','The organization enforces physical access authorizations to the information system in addition to the physical access controls for the facility at [Assignment: organization-defined physical spaces containing one or more components of the information system].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (509,'PHYSICAL ACCESS CONTROL | FACILITY / INFORMATION SYSTEM BOUNDARIES','The organization performs security checks [Assignment: organization-defined frequency] at the physical boundary of the facility or information system for unauthorized exfiltration of information or removal of information system components.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (510,'PHYSICAL ACCESS CONTROL | CONTINUOUS GUARDS / ALARMS / MONITORING','The organization employs guards and/or alarms to monitor every physical access point to the facility where the information system resides 24 hours per day, 7 days per week.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (511,'PHYSICAL ACCESS CONTROL | LOCKABLE CASINGS','The organization uses lockable physical casings to protect [Assignment: organization-defined information system components] from unauthorized physical access.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (512,'PHYSICAL ACCESS CONTROL | TAMPER PROTECTION','The organization employs [Assignment: organization-defined security safeguards] to [Selection (one or more): detect; prevent] physical tampering or alteration of [Assignment: organization-defined hardware components] within the information system.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (513,'PHYSICAL ACCESS CONTROL | FACILITY PENETRATION TESTING','The organization employs a penetration testing process that includes [Assignment: organization-defined frequency], unannounced attempts to bypass or circumvent security controls associated with physical access points to the facility.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (514,'ACCESS CONTROL FOR TRANSMISSION MEDIUM','The organization controls physical access to [Assignment: organization-defined information system distribution and transmission lines] within organizational facilities using [Assignment: organization-defined security safeguards].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (515,'ACCESS CONTROL FOR OUTPUT DEVICES','The organization controls physical access to information system output devices to prevent unauthorized individuals from obtaining the output.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (516,'ACCESS CONTROL FOR OUTPUT DEVICES | ACCESS TO OUTPUT BY AUTHORIZED INDIVIDUALS','The organization:(a) Controls physical access to output from [Assignment: organization-defined output devices]; and (b) Ensures that only authorized individuals receive output from the device.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (517,'ACCESS CONTROL FOR OUTPUT DEVICES | ACCESS TO OUTPUT BY INDIVIDUAL IDENTITY','The information system:(a) Controls physical access to output from [Assignment: organization-defined output devices]; and(b) Links individual identity to receipt of the output from the device.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (518,'ACCESS CONTROL FOR OUTPUT DEVICES | MARKING OUTPUT DEVICES','The organization marks [Assignment: organization-defined information system output devices] indicating the appropriate security marking of the information permitted to be output from the device.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (519,'MONITORING PHYSICAL ACCESS','The organization: a. Monitors physical access to the facility where the information system resides to detect and respond to physical security incidents; b. Reviews physical access logs [Assignment: organization-defined frequency] and upon occurrence of [Assignment: organization-defined events or potential indications of events]; and c. Coordinates results of reviews and investigations with the organizational incident response capability.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (520,'MONITORING PHYSICAL ACCESS | INTRUSION ALARMS / SURVEILLANCE EQUIPMENT','The organization monitors physical intrusion alarms and surveillance equipment.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (521,'MONITORING PHYSICAL ACCESS | AUTOMATED INTRUSION RECOGNITION / RESPONSES','The organization employs automated mechanisms to recognize [Assignment: organization-defined classes/types of intrusions] and initiate [Assignment: organization-defined response actions].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (522,'MONITORING PHYSICAL ACCESS | VIDEO SURVEILLANCE','The organization employs video surveillance of [Assignment: organization-defined operational areas] and retains video recordings for [Assignment: organization-defined time period].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (523,'MONITORING PHYSICAL ACCESS | MONITORING PHYSICAL ACCESS TO INFORMATION SYSTEMS','The organization monitors physical access to the information system in addition to the physical access monitoring of the facility as [Assignment: organization-defined physical spaces containing one or more components of the information system].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (524,'VISITOR CONTROL','Withdrawn: Incorporated into PE-2 and PE-3.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (525,'VISITOR ACCESS RECORDS','The organization: a. Maintains visitor access records to the facility where the information system resides for [Assignment: organization-defined time period]; and b. Reviews visitor access records [Assignment: organization-defined frequency].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (526,'VISITOR ACCESS RECORDS | AUTOMATED RECORDS MAINTENANCE / REVIEW','The organization employs automated mechanisms to facilitate the maintenance and review of visitor access records.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (527,'VISITOR ACCESS RECORDS | PHYSICAL ACCESS RECORDS','Withdrawn: Incorporated into PE-2.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (528,'POWER EQUIPMENT AND CABLING','The organization protects power equipment and power cabling for the information system from damage and destruction.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (529,'POWER EQUIPMENT AND CABLING | REDUNDANT CABLING','The organization employs redundant power cabling paths that are physically separated by [Assignment: organization-defined distance].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (530,'POWER EQUIPMENT AND CABLING | AUTOMATIC VOLTAGE CONTROLS','The organization employs automatic voltage controls for [Assignment: organization-defined critical information system components].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (531,'EMERGENCY SHUTOFF','The organization: a. Provides the capability of shutting off power to the information system or individual system components in emergency situations; b. Places emergency shutoff switches or devices in [Assignment: organization-defined location by information system or system component] to facilitate safe and easy access for personnel; and c. Protects emergency power shutoff capability from unauthorized activation.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (532,'EMERGENCY SHUTOFF | ACCIDENTAL / UNAUTHORIZED ACTIVATION','Withdrawn: Incorporated into PE-10.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (533,'EMERGENCY POWER','The organization provides a short-term uninterruptible power supply to facilitate [Selection (one or more): an orderly shutdown of the information system; transition of the information system to long-term alternate power] in the event of a primary power source loss.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (534,'EMERGENCY POWER | LONG-TERM ALTERNATE POWER SUPPLY - MINIMAL OPERATIONAL CAPABILITY','The organization provides a long-term alternate power supply for the information system that is capable of maintaining minimally required operational capability in the event of an extended loss of the primary power source.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (535,'EMERGENCY POWER | LONG-TERM ALTERNATE POWER SUPPLY - SELF-CONTAINED','The organization provides a long-term alternate power supply for the information system that is:(a) Self-contained;(b) Not reliant on external power generation; and (c) Capable of maintaining [Selection: minimally required operational capability; full operational capability] in the event of an extended loss of the primary power source.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (536,'EMERGENCY LIGHTING','The organization employs and maintains automatic emergency lighting for the information system that activates in the event of a power outage or disruption and that covers emergency exits and evacuation routes within the facility.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (537,'EMERGENCY LIGHTING | ESSENTIAL MISSIONS / BUSINESS FUNCTIONS','The organization provides emergency lighting for all areas within the facility supporting essential missions and business functions.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (538,'FIRE PROTECTION','The organization employs and maintains fire suppression and detection devices/systems for the information system that are supported by an independent energy source.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (539,'FIRE PROTECTION | DETECTION DEVICES / SYSTEMS','The organization employs fire detection devices/systems for the information system that activate automatically and notify [Assignment: organization-defined personnel or roles] and [Assignment: organization-defined emergency responders] in the event of a fire.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (540,'FIRE PROTECTION | SUPPRESSION DEVICES / SYSTEMS','The organization employs fire suppression devices/systems for the information system that provide automatic notification of any activation to Assignment: organization-defined personnel or roles] and [Assignment: organization-defined emergency responders].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (541,'FIRE PROTECTION | AUTOMATIC FIRE SUPPRESSION','The organization employs an automatic fire suppression capability for the information system when the facility is not staffed on a continuous basis.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (542,'FIRE PROTECTION | INSPECTIONS','The organization ensures that the facility undergoes [Assignment: organization-defined frequency] inspections by authorized and qualified inspectors and resolves identified deficiencies within [Assignment: organization-defined time period].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (543,'TEMPERATURE AND HUMIDITY CONTROLS','The organization:a. Maintains temperature and humidity levels within the facility where the information system resides at [Assignment: organization-defined acceptable levels]; and b. Monitors temperature and humidity levels [Assignment: organization-defined frequency].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (544,'TEMPERATURE AND HUMIDITY CONTROLS | AUTOMATIC CONTROLS','The organization employs automatic temperature and humidity controls in the facility to prevent fluctuations potentially harmful to the information system.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (545,'TEMPERATURE AND HUMIDITY CONTROLS | MONITORING WITH ALARMS / NOTIFICATIONS','The organization employs temperature and humidity monitoring that provides an alarm or notification of changes potentially harmful to personnel or equipment.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (546,'WATER DAMAGE PROTECTION','The organization protects the information system from damage resulting from water leakage by providing master shutoff or isolation valves that are accessible, working properly, and known to key personnel.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (547,'WATER DAMAGE PROTECTION | AUTOMATION SUPPORT','The organization employs automated mechanisms to detect the presence of water in the vicinity of the information system and alerts [Assignment: organization-defined personnel or roles].\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (548,'DELIVERY AND REMOVAL','The organization authorizes, monitors, and controls [Assignment: organization-defined types of information system components] entering and exiting the facility and maintains records of those items.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (549,'ALTERNATE WORK SITE','The organization: a. Employs [Assignment: organization-defined security controls] at alternate work sites; b. Assesses as feasible, the effectiveness of security controls at alternate work sites; and c. Provides a means for employees to communicate with information security personnel in case of security incidents or problems.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (550,'LOCATION OF INFORMATION SYSTEM COMPONENTS','The organization positions information system components within the facility to minimize potential damage from [Assignment: organization-defined physical and environmental hazards] and to minimize the opportunity for unauthorized access.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (551,'LOCATION OF INFORMATION SYSTEM COMPONENTS | FACILITY SITE','The organization plans the location or site of the facility where the information system resides with regard to physical and environmental hazards and for existing facilities, considers the physical and environmental hazards in its risk mitigation strategy.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (552,'INFORMATION LEAKAGE','The organization protects the information system from information leakage due to electromagnetic signals emanations.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (553,'INFORMATION LEAKAGE | NATIONAL EMISSIONS / TEMPEST POLICIES AND PROCEDURES','The organization ensures that information system components, associated data communications, and networks are protected in accordance with national emissions and TEMPEST policies and procedures based on the security category or classification of the information.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (554,'ASSET MONITORING AND TRACKING','The organization: a. Employs [Assignment: organization-defined asset location technologies] to track and monitor the location and movement of [Assignment: organization-defined assets] within [Assignment: organization-defined controlled areas]; and b. Ensures that asset location technologies are employed in accordance with applicable federal laws, Executive Orders, directives, regulations, policies, standards, and guidance.\n		',NULL,11);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (555,'SECURITY PLANNING POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A security planning policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the security planning policy and associated security planning controls; and\n			b. Reviews and updates the current:\n				1. Security planning policy [Assignment: organization-defined frequency]; and\n				2. Security planning procedures [Assignment: organization-defined frequency].\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (556,'SYSTEM SECURITY PLAN','The organization:\n			a. Develops a security plan for the information system that:\n				1. Is consistent with the organization’s enterprise architecture;\n				2. Explicitly defines the authorization boundary for the system;\n				3. Describes the operational context of the information system in terms of missions and business processes;\n				4. Provides the security categorization of the information system including supporting rationale;\n				5. Describes the operational environment for the information system and relationships with or connections to other information systems;\n				6. Provides an overview of the security requirements for the system;\n				7. Identifies any relevant overlays, if applicable;\n				8. Describes the security controls in place or planned for meeting those requirements including a rationale for the tailoring decisions; and\n				9. Is reviewed and approved by the authorizing official or designated representative prior to plan implementation;\n			b. Distributes copies of the security plan and communicates subsequent changes to the plan to [Assignment: organization-defined personnel or roles];\n			c. Reviews the security plan for the information system [Assignment: organization-defined frequency];\n			d. Updates the plan to address changes to the information system/environment of operation or problems identified during plan implementation or security control assessments; and\n			e. Protects the security plan from unauthorized disclosure and modification.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (557,'SYSTEM SECURITY PLAN | CONCEPT OF OPERATIONS','Withdrawn: Incorporated into PL-7.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (558,'SYSTEM SECURITY PLAN | FUNCTIONAL ARCHITECTURE','Withdrawn: Incorporated into PL-8.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (559,'SYSTEM SECURITY PLAN | PLAN / COORDINATE WITH OTHER ORGANIZATIONAL ENTITIES','The organization plans and coordinates security-related activities affecting the information system with [Assignment: organization-defined individuals or groups] before conducting such activities in order to reduce the impact on other organizational entities.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (560,'SYSTEM SECURITY PLAN UPDATE','Withdrawn: Incorporated into PL-2.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (561,'RULES OF BEHAVIOR','The organization:\n			a. Establishes and makes readily available to individuals requiring access to the information system, the rules that describe their responsibilities and expected behavior with regard to information and information system usage;\n			b. Receives a signed acknowledgment from such individuals, indicating that they have read, understand, and agree to abide by the rules of behavior, before authorizing access to information and the information system;\n			c. Reviews and updates the rules of behavior [Assignment: organization-defined frequency]; and\n			d. Requires individuals who have signed a previous version of the rules of behavior to read and re-sign when the rules of behavior are revised/updated.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (562,'RULES OF BEHAVIOR | SOCIAL MEDIA AND NETWORKING RESTRICTIONS','The organization includes in the rules of behavior, explicit restrictions on the use of social media/networking sites and posting organizational information on public websites.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (563,'PRIVACY IMPACT ASSESSMENT','Withdrawn: Incorporated into Appendix J, AR-2.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (564,'SECURITY-RELATED ACTIVITY PLANNING','Withdrawn: Incorporated into PL-2.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (565,'SECURITY CONCEPT OF OPERATIONS','The organization:a. Develops a security Concept of Operations (CONOPS) for the information system containing at a minimum, how the organization intends to operate the system from the perspective of information security; and b. Reviews and updates the CONOPS [Assignment: organization-defined frequency].\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (566,'INFORMATION SECURITY ARCHITECTURE','The organization:\n			a. Develops an information security architecture for the information system that:\n				1. Describes the overall philosophy, requirements, and approach to be taken with regard to protecting the confidentiality, integrity, and availability of organizational information;\n				2. Describes how the information security architecture is integrated into and supports the enterprise architecture; and\n				3. Describes any information security assumptions about, and dependencies on, external services;\n			b. Reviews and updates the information security architecture [Assignment: organization-defined frequency] to reflect updates in the enterprise architecture; and\n			c. Ensures that planned information security architecture changes are reflected in the security plan, the security Concept of Operations (CONOPS), and organizational procurements/acquisitions.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (567,'INFORMATION SECURITY ARCHITECTURE | DEFENSE-IN-DEPTH','The organization designs its security architecture using a defense-in-depth approach that: (a) Allocates [Assignment: organization-defined security safeguards] to [Assignment: organization-defined locations and architectural layers]; and (b) Ensures that the allocated security safeguards operate in a coordinated and mutually reinforcing manner.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (568,'INFORMATION SECURITY ARCHITECTURE | SUPPLIER DIVERSITY','The organization requires that [Assignment: organization-defined security safeguards] allocated to [Assignment: organization-defined locations and architectural layers] are obtained from different suppliers.\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (569,'CENTRAL MANAGEMENT','The organization centrally manages [Assignment: organization-defined security controls and related processes].\n		',NULL,12);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (570,'PERSONNEL SECURITY POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A personnel security policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the personnel security policy and associated personnel security controls; and\n			b. Reviews and updates the current:\n				1. Personnel security policy [Assignment: organization-defined frequency]; and\n				2. Personnel security procedures [Assignment: organization-defined frequency].\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (571,'POSITION RISK DESIGNATION','The organization: a. Assigns a risk designation to all organizational positions; b. Establishes screening criteria for individuals filling those positions; and c. Reviews and updates position risk designations [Assignment: organization-defined frequency].\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (572,'PERSONNEL SCREENING','The organization: a. Screens individuals prior to authorizing access to the information system; and b. Rescreens individuals according to [Assignment: organization-defined conditions requiring rescreening and, where rescreening is so indicated, the frequency of such rescreening].\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (573,'PERSONNEL SCREENING | CLASSIFIED INFORMATION','The organization ensures that individuals accessing an information system processing, storing, or transmitting classified information are cleared and indoctrinated to the highest classification level of the information to which they have access on the system.\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (574,'PERSONNEL SCREENING | FORMAL INDOCTRINATION','The organization ensures that individuals accessing an information system processing, storing, or transmitting types of classified information which require formal indoctrination, are formally indoctrinated for all of the relevant types of information to which they have access on the system\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (575,'PERSONNEL SCREENING | INFORMATION WITH SPECIAL PROTECTION MEASURES','The organization ensures that individuals accessing an information system processing, storing, or transmitting information requiring special protection: (a) Have valid access authorizations that are demonstrated by assigned official government duties; and (b) Satisfy [Assignment: organization-defined additional personnel screening criteria].\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (576,'PERSONNEL TERMINATION','The organization, upon termination of individual employment:\n			a. Disables information system access within [Assignment: organization-defined time period];\n			b. Terminates/revokes any authenticators/credentials associated with the individual;\n			c. Conducts exit interviews that include a discussion of [Assignment: organization-defined information security topics];\n			d. Retrieves all security-related organizational information system-related property;\n			e. Retains access to organizational information and information systems formerly controlled by terminated individual; and\n			f. Notifies [Assignment: organization-defined personnel or roles] within [Assignment: organization-defined time period].\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (577,'PERSONNEL TERMINATION | POST-EMPLOYMENT REQUIREMENTS','The organization: (a) Notifies terminated individuals of applicable, legally binding post-employment requirements for the protection of organizational information; and (b) Requires terminated individuals to sign an acknowledgment of post-employment requirements as part of the organizational termination process.\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (578,'PERSONNEL TERMINATION | AUTOMATED NOTIFICATION','The organization employs automated mechanisms to notify [Assignment: organization-defined personnel or roles] upon termination of an individual.\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (579,'PERSONNEL TRANSFER','The organization: a. Reviews and confirms ongoing operational need for current logical and physical access authorizations to information systems/facilities when individuals are reassigned or transferred to other positions within the organization; b. Initiates [Assignment: organization-defined transfer or reassignment actions] within [Assignment: organization-defined time period following the formal transfer action]; c. Modifies access authorization as needed to correspond with any changes in operational need due to reassignment or transfer; and d. Notifies [Assignment: organization-defined personnel or roles] within [Assignment: organization-defined time period].\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (580,'ACCESS AGREEMENTS','The organization: \n			a. Develops and documents access agreements for organizational information systems;\n			b. Reviews and updates the access agreements [Assignment: organization-defined frequency]; and\n			c. Ensures that individuals requiring access to organizational information and information systems:\n				1. Sign appropriate access agreements prior to being granted access; and\n				2. Re-sign access agreements to maintain access to organizational information systems when access agreements have been updated or [Assignment: organization-defined frequency].\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (581,'ACCESS AGREEMENTS | INFORMATION REQUIRING SPECIAL PROTECTION','Withdrawn: Incorporated into PS-3.\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (582,'ACCESS AGREEMENTS | CLASSIFIED INFORMATION REQUIRING SPECIAL PROTECTION','The organization ensures that access to classified information requiring special protection is granted only to individuals who: (a) Have a valid access authorization that is demonstrated by assigned official government duties; (b) Satisfy associated personnel security criteria; and (c) Have read, understood, and signed a nondisclosure agreement.\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (583,'ACCESS AGREEMENTS | POST-EMPLOYMENT REQUIREMENTS','The organization:(a) Notifies individuals of applicable, legally binding post-employment requirements for protection of organizational information; and (b) Requires individuals to sign an acknowledgment of these requirements, if applicable, as part of granting initial access to covered information.\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (584,'THIRD-PARTY PERSONNEL SECURITY','The organization:\n			a. Establishes personnel security requirements including security roles and responsibilities for third-party providers;\n			b. Requires third-party providers to comply with personnel security policies and procedures established by the organization;\n			c. Documents personnel security requirements;\n			d. Requires third-party providers to notify [Assignment: organization-defined personnel or roles] of any personnel transfers or terminations of third-party personnel who possess organizational credentials and/or badges, or who have information system privileges within [Assignment: organization-defined time period]; and\n			e. Monitors provider compliance.\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (585,'PERSONNEL SANCTIONS','The organization: a. Employs a formal sanctions process for individuals failing to comply with established information security policies and procedures; and b. Notifies [Assignment: organization-defined personnel or roles] within [Assignment: organization-defined time period] when a formal employee sanctions process is initiated, identifying the individual sanctioned and the reason for the sanction.\n		',NULL,13);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (586,'RISK ASSESSMENT POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A risk assessment policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the risk assessment policy and associated risk assessment controls; and\n			b. Reviews and updates the current:\n				1. Risk assessment policy [Assignment: organization-defined frequency]; and\n				2. Risk assessment procedures [Assignment: organization-defined frequency].\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (587,'SECURITY CATEGORIZATION','The organization: a. Categorizes information and the information system in accordance with applicable federal laws, Executive Orders, directives, policies, regulations, standards, and guidance; b. Documents the security categorization results (including supporting rationale) in the security plan for the information system; and c. Ensures that the authorizing official or authorizing official designated representative reviews and approves the security categorization decision.\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (588,'RISK ASSESSMENT','The organization:\n			a. Conducts an assessment of risk, including the likelihood and magnitude of harm, from the unauthorized access, use, disclosure, disruption, modification, or destruction of the information system and the information it processes, stores, or transmits;\n			b. Documents risk assessment results in [Selection: security plan; risk assessment report; [Assignment: organization-defined document]];\n			c. Reviews risk assessment results [Assignment: organization-defined frequency];\n			d. Disseminates risk assessment results to [Assignment: organization-defined personnel or roles]; and\n			e. Updates the risk assessment [Assignment: organization-defined frequency] or whenever there are significant changes to the information system or environment of operation (including the identification of new threats and vulnerabilities), or other conditions that may impact the security state of the system.\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (589,'RISK ASSESSMENT UPDATE','Withdrawn: Incorporated into RA-3.\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (590,'VULNERABILITY SCANNING','The organization:\n			a. Scans for vulnerabilities in the information system and hosted applications [Assignment: organization-defined frequency and/or randomly in accordance with organization-defined process] and when new vulnerabilities potentially affecting the system/applications are identified and reported;\n			b. Employs vulnerability scanning tools and techniques that facilitate interoperability among tools and automate parts of the vulnerability management process by using standards for:\n				1. Enumerating platforms, software flaws, and improper configurations;\n				2. Formatting checklists and test procedures; and\n				3. Measuring vulnerability impact;\n			c. Analyzes vulnerability scan reports and results from security control assessments;\n			d. Remediates legitimate vulnerabilities [Assignment: organization-defined response times] in accordance with an organizational assessment of risk; and\n			e. Shares information obtained from the vulnerability scanning process and security control assessments with [Assignment: organization-defined personnel or roles] to help eliminate similar vulnerabilities in other information systems (i.e., systemic weaknesses or deficiencies).\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (591,'VULNERABILITY SCANNING | UPDATE TOOL CAPABILITY','The organization employs vulnerability scanning tools that include the capability to readily update the information system vulnerabilities to be scanned.\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (592,'VULNERABILITY SCANNING | UPDATE BY FREQUENCY / PRIOR TO NEW SCAN / WHEN IDENTIFIED','The organization updates the information system vulnerabilities scanned [Selection (one or more): [Assignment: organization-defined frequency]; prior to a new scan; when new vulnerabilities are identified and reported].\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (593,'VULNERABILITY SCANNING | BREADTH / DEPTH OF COVERAGE','The organization employs vulnerability scanning procedures that can identify the breadth and depth of coverage (i.e., information system components scanned and vulnerabilities checked).\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (594,'VULNERABILITY SCANNING | DISCOVERABLE INFORMATION','The organization determines what information about the information system is discoverable by adversaries and subsequently takes [Assignment: organization-defined corrective actions].\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (595,'VULNERABILITY SCANNING | PRIVILEGED ACCESS','The information system implements privileged access authorization to [Assignment: organization-identified information system components] for selected [Assignment: organization-defined vulnerability scanning activities].\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (596,'VULNERABILITY SCANNING | AUTOMATED TREND ANALYSES','The organization employs automated mechanisms to compare the results of vulnerability scans over time to determine trends in information system vulnerabilities.\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (597,'VULNERABILITY SCANNING | AUTOMATED DETECTION AND NOTIFICATION OF UNAUTHORIZED COMPONENTS','Withdrawn: Incorporated into CM-8.\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (598,'VULNERABILITY SCANNING | REVIEW HISTORIC AUDIT LOGS','The organization reviews historic audit logs to determine if a vulnerability identified in the information system has been previously exploited.\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (599,'VULNERABILITY SCANNING | PENETRATION TESTING AND ANALYSES','Withdrawn: Incorporated into CA-8.\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (600,'VULNERABILITY SCANNING | CORRELATE SCANNING INFORMATION','The organization correlates the output from vulnerability scanning tools to determine the presence of multi-vulnerability/multi-hop attack vectors.\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (601,'TECHNICAL SURVEILLANCE COUNTERMEASURES SURVEY','The organization employs a technical surveillance countermeasures survey at [Assignment: organization-defined locations] [Selection (one or more): [Assignment: organization-defined frequency]; [Assignment: organization-defined events or indicators occur]].\n		',NULL,14);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (602,'SYSTEM AND SERVICES ACQUISITION POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A system and services acquisition policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the system and services acquisition policy and associated system and services acquisition controls; and\n			b. Reviews and updates the current:\n				1. System and services acquisition policy [Assignment: organization-defined frequency]; and\n				2. System and services acquisition procedures [Assignment: organization-defined frequency].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (603,'ALLOCATION OF RESOURCES','The organization: a. Determines information security requirements for the information system or information system service in mission/business process planning; b. Determines, documents, and allocates the resources required to protect the information system or information system service as part of its capital planning and investment control process; and c. Establishes a discrete line item for information security in organizational programming and budgeting documentation.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (604,'SYSTEM DEVELOPMENT LIFE CYCLE','The organization: a. Manages the information system using [Assignment: organization-defined system development life cycle] that incorporates information security considerations; b. Defines and documents information security roles and responsibilities throughout the system development life cycle; c. Identifies individuals having information security roles and responsibilities; and d. Integrates the organizational information security risk management process into system development life cycle activities.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (605,'ACQUISITION PROCESS','The organization includes the following requirements, descriptions, and criteria, explicitly or by reference, in the acquisition contract for the information system, system component, or information system service in accordance with applicable federal laws, Executive Orders, directives, policies, regulations, standards, guidelines, and organizational mission/business needs:a. Security functional requirements; b. Security strength requirements; c. Security assurance requirements; d. Security-related documentation requirements; e. Requirements for protecting security-related documentation; f. Description of the information system development environment and environment in which the system is intended to operate; and g. Acceptance criteria.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (606,'ACQUISITION PROCESS | FUNCTIONAL PROPERTIES OF SECURITY CONTROLS','The organization requires the developer of the information system, system component, or information system service to provide a description of the functional properties of the security controls to be employed.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (607,'ACQUISITION PROCESS | DESIGN / IMPLEMENTATION INFORMATION FOR SECURITY CONTROLS','The organization requires the developer of the information system, system component, or information system service to provide design and implementation information for the security controls to be employed that includes: [Selection (one or more): security-relevant external system interfaces; high-level design; low-level design; source code or hardware schematics; [Assignment: organization-defined design/implementation information]] at [Assignment: organization-defined level of detail].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (608,'ACQUISITION PROCESS | DEVELOPMENT METHODS / TECHNIQUES / PRACTICES','The organization requires the developer of the information system, system component, or information system service to demonstrate the use of a system development life cycle that includes [Assignment: organization-defined state-of-the-practice system/security engineering methods, software development methods, testing/evaluation/validation techniques, and quality control processes].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (609,'ACQUISITION PROCESS | ASSIGNMENT OF COMPONENTS TO SYSTEMS','Withdrawn: Incorporated into CM-8 (9).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (610,'ACQUISITION PROCESS | SYSTEM / COMPONENT / SERVICE CONFIGURATIONS','The organization requires the developer of the information system, system component, or information system service to: (a) Deliver the system, component, or service with [Assignment: organization-defined security configurations] implemented; and (b) Use the configurations as the default for any subsequent system, component, or service reinstallation or upgrade.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (611,'ACQUISITION PROCESS | USE OF INFORMATION ASSURANCE PRODUCTS','The organization: (a) Employs only government off-the-shelf (GOTS) or commercial off-the-shelf (COTS) information assurance (IA) and IA-enabled information technology products that compose an NSA-approved solution to protect classified information when the networks used to transmit the information are at a lower classification level than the information being transmitted; and (b) Ensures that these products have been evaluated and/or validated by NSA or in accordance with NSA-approved procedures.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (612,'ACQUISITION PROCESS | NIAP-APPROVED PROTECTION PROFILES','The organization: (a) Limits the use of commercially provided information assurance (IA) and IA-enabled information technology products to those products that have been successfully evaluated against a National Information Assurance partnership (NIAP)-approved Protection Profile for a specific technology type, if such a profile exists; and (b) Requires, if no NIAP-approved Protection Profile exists for a specific technology type but a commercially provided information technology product relies on cryptographic functionality to enforce its security policy, that the cryptographic module is FIPS-validated.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (613,'ACQUISITION PROCESS | CONTINUOUS MONITORING PLAN','The organization requires the developer of the information system, system component, or information system service to produce a plan for the continuous monitoring of security control effectiveness that contains [Assignment: organization-defined level of detail].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (614,'ACQUISITION PROCESS | FUNCTIONS / PORTS / PROTOCOLS / SERVICES IN USE','The organization requires the developer of the information system, system component, or information system service to identify early in the system development life cycle, the functions, ports, protocols, and services intended for organizational use.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (615,'ACQUISITION PROCESS | USE OF APPROVED PIV PRODUCTS','The organization employs only information technology products on the FIPS 201-approved products list for Personal Identity Verification (PIV) capability implemented within organizational information systems.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (616,'INFORMATION SYSTEM DOCUMENTATION','The organization:\n			a. Obtains administrator documentation for the information system, system component, or information system service that describes:\n				1. Secure configuration, installation, and operation of the system, component, or service;\n				2. Effective use and maintenance of security functions/mechanisms; and\n				3. Known vulnerabilities regarding configuration and use of administrative (i.e., privileged) functions;\n			b. Obtains user documentation for the information system, system component, or information system service that describes:\n				1. User-accessible security functions/mechanisms and how to effectively use those security functions/mechanisms;\n				2. Methods for user interaction, which enables individuals to use the system, component, or service in a more secure manner; and\n				3. User responsibilities in maintaining the security of the system, component, or service;\n			c. Documents attempts to obtain information system, system component, or information system service documentation when such documentation is either unavailable or nonexistent and takes [Assignment: organization-defined actions] in response;\n			d. Protects documentation as required, in accordance with the risk management strategy; and\n			e. Distributes documentation to [Assignment: organization-defined personnel or roles].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (617,'INFORMATION SYSTEM DOCUMENTATION | FUNCTIONAL PROPERTIES OF SECURITY CONTROLS','Withdrawn: Incorporated into SA-4 (1).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (618,'INFORMATION SYSTEM DOCUMENTATION | SECURITY-RELEVANT EXTERNAL SYSTEM INTERFACES','Withdrawn: Incorporated into SA-4 (2).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (619,'INFORMATION SYSTEM DOCUMENTATION | HIGH-LEVEL DESIGN','Withdrawn: Incorporated into SA-4 (2).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (620,'INFORMATION SYSTEM DOCUMENTATION | LOW-LEVEL DESIGN','Withdrawn: Incorporated into SA-4 (2).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (621,'INFORMATION SYSTEM DOCUMENTATION | SOURCE CODE','Withdrawn: Incorporated into SA-4 (2).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (622,'SOFTWARE USAGE RESTRICTIONS','Withdrawn: Incorporated into CM-10 and SI-7.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (623,'USER-INSTALLED SOFTWARE','Withdrawn: Incorporated into CM-11 and SI-7.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (624,'SECURITY ENGINEERING PRINCIPLES','The organization applies information system security engineering principles in the specification, design, development, implementation, and modification of the information system.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (625,'EXTERNAL INFORMATION SYSTEM SERVICES','The organization: a. Requires that providers of external information system services comply with organizational information security requirements and employ [Assignment: organization-defined security controls] in accordance with applicable federal laws, Executive Orders, directives, policies, regulations, standards, and guidance; b. Defines and documents government oversight and user roles and responsibilities with regard to external information system services; and c. Employs [Assignment: organization-defined processes, methods, and techniques] to monitor security control compliance by external service providers on an ongoing basis.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (626,'EXTERNAL INFORMATION SYSTEMS | RISK ASSESSMENTS / ORGANIZATIONAL APPROVALS','The organization: (a) Conducts an organizational assessment of risk prior to the acquisition or outsourcing of dedicated information security services; and (b) Ensures that the acquisition or outsourcing of dedicated information security services is approved by [Assignment: organization-defined personnel or roles].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (627,'EXTERNAL INFORMATION SYSTEMS | IDENTIFICATION OF FUNCTIONS / PORTS / PROTOCOLS / SERVICES','The organization requires providers of [Assignment: organization-defined external information system services] to identify the functions, ports, protocols, and other services required for the use of such services.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (628,'EXTERNAL INFORMATION SYSTEMS | ESTABLISH / MAINTAIN TRUST RELATIONSHIP WITH PROVIDERS','The organization establishes, documents, and maintains trust relationships with external service providers based on [Assignment: organization-defined security requirements, properties, factors, or conditions defining acceptable trust relationships].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (629,'EXTERNAL INFORMATION SYSTEMS | CONSISTENT INTERESTS OF CONSUMERS AND PROVIDERS','The organization employs [Assignment: organization-defined security safeguards] to ensure that the interests of [Assignment: organization-defined external service providers] are consistent with and reflect organizational interests.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (630,'EXTERNAL INFORMATION SYSTEMS | PROCESSING, STORAGE, AND SERVICE LOCATION','The organization restricts the location of [Selection (one or more): information processing; information/data; information system services] to [Assignment: organization-defined locations] based on [Assignment: organization-defined requirements or conditions].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (631,'DEVELOPER CONFIGURATION MANAGEMENT','The organization requires the developer of the information system, system component, or information system service to:\n			a. Perform configuration management during system, component, or service [Selection (one or more): design; development; implementation; operation];\n			b. Document, manage, and control the integrity of changes to [Assignment: organization-defined configuration items under configuration management];\n			c. Implement only organization-approved changes to the system, component, or service;\n			d. Document approved changes to the system, component, or service and the potential security impacts of such changes; and\n			e. Track security flaws and flaw resolution within the system, component, or service and report findings to [Assignment: organization-defined personnel].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (632,'DEVELOPER CONFIGURATION MANAGEMENT | SOFTWARE / FIRMWARE INTEGRITY VERIFICATION','The organization requires the developer of the information system, system component, or information system service to enable integrity verification of software and firmware components.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (633,'DEVELOPER CONFIGURATION MANAGEMENT | ALTERNATIVE CONFIGURATION MANAGEMENT PROCESSES','The organization provides an alternate configuration management process using organizational personnel in the absence of a dedicated developer configuration management team.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (634,'DEVELOPER CONFIGURATION MANAGEMENT | HARDWARE INTEGRITY VERIFICATION','The organization requires the developer of the information system, system component, or information system service to enable integrity verification of hardware components.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (635,'DEVELOPER CONFIGURATION MANAGEMENT | TRUSTED GENERATION','The organization requires the developer of the information system, system component, or information system service to employ tools for comparing newly generated versions of security-relevant hardware descriptions and software/firmware source and object code with previous versions.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (636,'DEVELOPER CONFIGURATION MANAGEMENT | MAPPING INTEGRITY FOR VERSION CONTROL','The organization requires the developer of the information system, system component, or information system service to maintain the integrity of the mapping between the master build data (hardware drawings and software/firmware code) describing the current version of security-relevant hardware, software, and firmware and the on-site master copy of the data for the current version.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (637,'DEVELOPER CONFIGURATION MANAGEMENT | TRUSTED DISTRIBUTION','The organization requires the developer of the information system, system component, or information system service to execute procedures for ensuring that security-relevant hardware, software, and firmware updates distributed to the organization are exactly as specified by the master copies.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (638,'DEVELOPER SECURITY TESTING AND EVALUATION','The organization requires the developer of the information system, system component, or information system service to:\n			a. Create and implement a security assessment plan;\n			b. Perform [Selection (one or more): unit; integration; system; regression] testing/evaluation at [Assignment: organization-defined depth and coverage];\n			c. Produce evidence of the execution of the security assessment plan and the results of the security testing/evaluation;\n			d. Implement a verifiable flaw remediation process; and\n			e. Correct flaws identified during security testing/evaluation.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (639,'DEVELOPER SECURITY TESTING AND EVALUATION | STATIC CODE ANALYSIS','The organization requires the developer of the information system, system component, or information system service to employ static code analysis tools to identify common flaws and document the results of the analysis.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (640,'DEVELOPER SECURITY TESTING AND EVALUATION | THREAT AND VULNERABILITY ANALYSES','The organization requires the developer of the information system, system component, or information system service to perform threat and vulnerability analyses and subsequent testing/evaluation of the as-built system, component, or service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (641,'DEVELOPER SECURITY TESTING AND EVALUATION | INDEPENDENT VERIFICATION OF ASSESSMENT PLANS / EVIDENCE','The organization: (a) Requires an independent agent satisfying [Assignment: organization-defined independence criteria] to verify the correct implementation of the developer security assessment plan and the evidence produced during security testing/evaluation; and (b) Ensures that the independent agent is either provided with sufficient information to complete the verification process or granted the authority to obtain such information.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (642,'DEVELOPER SECURITY TESTING AND EVALUATION | MANUAL CODE REVIEWS','The organization requires the developer of the information system, system component, or information system service to perform a manual code review of [Assignment: organization-defined specific code] using [Assignment: organization-defined processes, procedures, and/or techniques].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (643,'DEVELOPER SECURITY TESTING AND EVALUATION | PENETRATION TESTING','The organization requires the developer of the information system, system component, or information system service to perform penetration testing at [Assignment: organization-defined breadth/depth] and with [Assignment: organization-defined constraints].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (644,'DEVELOPER SECURITY TESTING AND EVALUATION | ATTACK SURFACE REVIEWS','The organization requires the developer of the information system, system component, or information system service to perform attack surface reviews.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (645,'DEVELOPER SECURITY TESTING AND EVALUATION | VERIFY SCOPE OF TESTING / EVALUATION','The organization requires the developer of the information system, system component, or information system service to verify that the scope of security testing/evaluation provides complete coverage of required security controls at [Assignment: organization-defined depth of testing/evaluation].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (646,'DEVELOPER SECURITY TESTING AND EVALUATION | DYNAMIC CODE ANALYSIS','The organization requires the developer of the information system, system component, or information system service to employ dynamic code analysis tools to identify common flaws and document the results of the analysis.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (647,'SUPPLY CHAIN PROTECTION','The organization protects against supply chain threats to the information system, system component, or information system service by employing [Assignment: organization-defined security safeguards] as part of a comprehensive, defense-in-breadth information security strategy.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (648,'SUPPLY CHAIN PROTECTION | ACQUISITION STRATEGIES / TOOLS / METHODS','The organization employs [Assignment: organization-defined tailored acquisition strategies, contract tools, and procurement methods] for the purchase of the information system, system component, or information system service from suppliers.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (649,'SUPPLY CHAIN PROTECTION | SUPPLIER REVIEWS','The organization conducts a supplier review prior to entering into a contractual agreement to acquire the information system, system component, or information system service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (650,'SUPPLY CHAIN PROTECTION | TRUSTED SHIPPING AND WAREHOUSING','Withdrawn: Incorporated into SA-12 (1).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (651,'SUPPLY CHAIN PROTECTION | DIVERSITY OF SUPPLIERS','Withdrawn: Incorporated into SA-12 (13).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (652,'SUPPLY CHAIN PROTECTION | LIMITATION OF HARM','The organization employs [Assignment: organization-defined security safeguards] to limit harm from potential adversaries identifying and targeting the organizational supply chain.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (653,'SUPPLY CHAIN PROTECTION | MINIMIZING PROCUREMENT TIME','Withdrawn: Incorporated into SA-12 (1).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (654,'SUPPLY CHAIN PROTECTION | ASSESSMENTS PRIOR TO SELECTION / ACCEPTANCE / UPDATE','The organization conducts an assessment of the information system, system component, or information system service prior to selection, acceptance, or update.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (655,'SUPPLY CHAIN PROTECTION | USE OF ALL-SOURCE INTELLIGENCE','The organization uses all-source intelligence analysis of suppliers and potential suppliers of the information system, system component, or information system service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (656,'SUPPLY CHAIN PROTECTION | OPERATIONS SECURITY','The organization employs [Assignment: organization-defined Operations Security (OPSEC) safeguards] in accordance with classification guides to protect supply chain-related information for the information system, system component, or information system service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (657,'SUPPLY CHAIN PROTECTION | VALIDATE AS GENUINE AND NOT ALTERED','The organization employs [Assignment: organization-defined security safeguards] to validate that the information system or system component received is genuine and has not been altered.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (658,'SUPPLY CHAIN PROTECTION | PENETRATION TESTING / ANALYSIS OF ELEMENTS, PROCESSES, AND ACTORS','The organization employs [Selection (one or more): organizational analysis, independent third-party analysis, organizational penetration testing, independent third-party penetration testing] of [Assignment: organization-defined supply chain elements, processes, and actors] associated with the information system, system component, or information system service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (659,'SUPPLY CHAIN PROTECTION | INTER-ORGANIZATIONAL AGREEMENTS','The organization establishes inter-organizational agreements and procedures with entities involved in the supply chain for the information system, system component, or information system service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (660,'SUPPLY CHAIN PROTECTION | CRITICAL INFORMATION SYSTEM COMPONENTS','The organization employs [Assignment: organization-defined security safeguards] to ensure an adequate supply of [Assignment: organization-defined critical information system components].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (661,'SUPPLY CHAIN PROTECTION | IDENTITY AND TRACEABILITY','The organization establishes and retains unique identification of [Assignment: organization-defined supply chain elements, processes, and actors] for the information system, system component, or information system service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (662,'SUPPLY CHAIN PROTECTION | PROCESSES TO ADDRESS WEAKNESSES OR DEFICIENCIES','The organization establishes a process to address weaknesses or deficiencies in supply chain elements identified during independent or organizational assessments of such elements.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (663,'TRUSTWORTHINESS','The organization: a. Describes the trustworthiness required in the [Assignment: organization-defined information system, information system component, or information system service] supporting its critical missions/business functions; and b. Implements [Assignment: organization-defined assurance overlay] to achieve such trustworthiness.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (664,'CRITICALITY ANALYSIS','The organization identifies critical information system components and functions by performing a criticality analysis for [Assignment: organization-defined information systems, information system components, or information system services] at [Assignment: organization-defined decision points in the system development life cycle].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (665,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS','The organization:\n			a. Requires the developer of the information system, system component, or information system service to follow a documented development process that:\n				1. Explicitly addresses security requirements;\n				2. Identifies the standards and tools used in the development process;\n				3. Documents the specific tool options and tool configurations used in the development process; and\n				4. Documents, manages, and ensures the integrity of changes to the process and/or tools used in development; and\n			b. Reviews the development process, standards, tools, and tool options/configurations [Assignment: organization-defined frequency] to determine if the process, standards, tools, and tool options/configurations selected and employed can satisfy [Assignment: organization-defined security requirements].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (666,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | QUALITY METRICS','The organization requires the developer of the information system, system component, or information system service to: (a) Define quality metrics at the beginning of the development process; and (b) Provide evidence of meeting the quality metrics [Selection (one or more): [Assignment: organization-defined frequency]; [Assignment: organization-defined program review milestones]; upon delivery].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (667,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | SECURITY TRACKING TOOLS','The organization requires the developer of the information system, system component, or information system service to select and employ a security tracking tool for use during the development process.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (668,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | CRITICALITY ANALYSIS','The organization requires the developer of the information system, system component, or information system service to perform a criticality analysis at [Assignment: organization-defined breadth/depth] and at [Assignment: organization-defined decision points in the system development life cycle].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (669,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | THREAT MODELING / VULNERABILITY ANALYSIS','The organization requires that developers perform threat modeling and a vulnerability analysis for the information system at [Assignment: organization-defined breadth/depth] that: (a) Uses [Assignment: organization-defined information concerning impact, environment of operations, known or assumed threats, and acceptable risk levels]; (b) Employs [Assignment: organization-defined tools and methods]; and (c) Produces evidence that meets [Assignment: organization-defined acceptance criteria].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (670,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | ATTACK SURFACE REDUCTION','The organization requires the developer of the information system, system component, or information system service to reduce attack surfaces to [Assignment: organization-defined thresholds].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (671,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | CONTINUOUS IMPROVEMENT','The organization requires the developer of the information system, system component, or information system service to implement an explicit process to continuously improve the development process.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (672,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | AUTOMATED VULNERABILITY ANALYSIS','The organization requires the developer of the information system, system component, or information system service to: (a) Perform an automated vulnerability analysis using [Assignment: organization-defined tools]; (b) Determine the exploitation potential for discovered vulnerabilities; (c) Determine potential risk mitigations for delivered vulnerabilities; and (d) Deliver the outputs of the tools and results of the analysis to [Assignment: organization-defined personnel or roles].		\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (673,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | REUSE OF THREAT / VULNERABILITY INFORMATION','The organization requires the developer of the information system, system component, or information system service to use threat modeling and vulnerability analyses from similar systems, components, or services to inform the current development process.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (674,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | USE OF LIVE DATA','The organization approves, documents, and controls the use of live data in development and test environments for the information system, system component, or information system service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (675,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | INCIDENT RESPONSE PLAN','The organization requires the developer of the information system, system component, or information system service to provide an incident response plan.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (676,'DEVELOPMENT PROCESS, STANDARDS, AND TOOLS | ARCHIVE INFORMATION SYSTEM / COMPONENT','The organization requires the developer of the information system or system component to archive the system or component to be released or delivered together with the corresponding evidence supporting the final security review.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (677,'DEVELOPER-PROVIDED TRAINING','The organization requires the developer of the information system, system component, or information system service to provide [Assignment: organization-defined training] on the correct use and operation of the implemented security functions, controls, and/or mechanisms.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (678,'DEVELOPER SECURITY ARCHITECTURE AND DESIGN','The organization requires the developer of the information system, system component, or information system service to produce a design specification and security architecture that: a. Is consistent with and supportive of the organization’s security architecture which is established within and is an integrated part of the organization’s enterprise architecture; b. Accurately and completely describes the required security functionality, and the allocation of security controls among physical and logical components; and c. Expresses how individual security functions, mechanisms, and services work together to provide required security capabilities and a unified approach to protection.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (679,'DEVELOPER SECURITY ARCHITECTURE AND DESIGN | FORMAL POLICY MODEL','The organization requires the developer of the information system, system component, or information system service to: (a) Produce, as an integral part of the development process, a formal policy model describing the [Assignment: organization-defined elements of organizational security policy] to be enforced; and (b) Prove that the formal policy model is internally consistent and sufficient to enforce the defined elements of the organizational security policy when implemented. \n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (680,'DEVELOPER SECURITY ARCHITECTURE AND DESIGN | SECURITY-RELEVANT COMPONENTS','The organization requires the developer of the information system, system component, or information system service to: (a) Define security-relevant hardware, software, and firmware; and (b) Provide a rationale that the definition for security-relevant hardware, software, and firmware is complete.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (681,'DEVELOPER SECURITY ARCHITECTURE AND DESIGN | FORMAL CORRESPONDENCE','The organization requires the developer of the information system, system component, or information system service to:\n			(a) Produce, as an integral part of the development process, a formal top-level specification that specifies the interfaces to security-relevant hardware, software, and firmware in terms of exceptions, error messages, and effects;\n			(b) Show via proof to the extent feasible with additional informal demonstration as necessary, that the formal top-level specification is consistent with the formal policy model;\n			(c) Show via informal demonstration, that the formal top-level specification completely covers the interfaces to security-relevant hardware, software, and firmware;\n			(d) Show that the formal top-level specification is an accurate description of the implemented security-relevant hardware, software, and firmware; and\n			(e) Describe the security-relevant hardware, software, and firmware mechanisms not addressed in the formal top-level specification but strictly internal to the security-relevant hardware, software, and firmware.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (682,'DEVELOPER SECURITY ARCHITECTURE AND DESIGN | INFORMAL CORRESPONDENCE','The organization requires the developer of the information system, system component, or information system service to:(a) Produce, as an integral part of the development process, an informal descriptive top-level specification that specifies the interfaces to security-relevant hardware, software, and firmware in terms of exceptions, error messages, and effects;			(b) Show via [Selection: informal demonstration, convincing argument with formal methods as feasible] that the descriptive top-level specification is consistent with the formal policy model;			(c) Show via informal demonstration, that the descriptive top-level specification completely covers the interfaces to security-relevant hardware, software, and firmware;			(d) Show that the descriptive top-level specification is an accurate description of the interfaces to security-relevant hardware, software, and firmware; and			(e) Describe the security-relevant hardware, software, and firmware mechanisms not addressed in the descriptive top-level specification but strictly internal to the security-relevant hardware, software, and firmware.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (683,'DEVELOPER SECURITY ARCHITECTURE AND DESIGN | CONCEPTUALLY SIMPLE DESIGN','The organization requires the developer of the information system, system component, or information system service to: (a) Design and structure the security-relevant hardware, software, and firmware to use a complete, conceptually simple protection mechanism with precisely defined semantics; and (b) Internally structure the security-relevant hardware, software, and firmware with specific regard for this mechanism.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (684,'DEVELOPER SECURITY ARCHITECTURE AND DESIGN | CONCEPTUALLY SIMPLE DESIGN','The organization requires the developer of the information system, system component, or information system service to structure security-relevant hardware, software, and firmware to facilitate testing.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (685,'DEVELOPER SECURITY ARCHITECTURE AND DESIGN | CONCEPTUALLY SIMPLE DESIGN','The organization requires the developer of the information system, system component, or information system service to structure security-relevant hardware, software, and firmware to facilitate controlling access with least privilege.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (686,'TAMPER RESISTANCE AND DETECTION','The organization implements a tamper protection program for the information system, system component, or information system service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (687,'TAMPER RESISTANCE AND DETECTION | MULTIPLE PHASES OF SDLC','The organization employs anti-tamper technologies and techniques during multiple phases in the system development life cycle including design, development, integration, operations, and maintenance.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (688,'TAMPER RESISTANCE AND DETECTION | INSPECTION OF INFORMATION SYSTEMS, COMPONENTS, OR DEVICES','The organization inspects [Assignment: organization-defined information systems, system components, or devices] [Selection (one or more): at random; at [Assignment: organization-defined frequency], upon [Assignment: organization-defined indications of need for inspection]] to detect tampering.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (689,'COMPONENT AUTHENTICITY','The organization: a. Develops and implements anti-counterfeit policy and procedures that include the means to detect and prevent counterfeit components from entering the information system; and b. Reports counterfeit information system components to [Selection (one or more): source of counterfeit component; [Assignment: organization-defined external reporting organizations]; [Assignment: organization-defined personnel or roles]].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (690,'COMPONENT AUTHENTICITY | ANTI-COUNTERFEIT TRAINING','The organization trains [Assignment: organization-defined personnel or roles] to detect counterfeit information system components (including hardware, software, and firmware).\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (691,'COMPONENT AUTHENTICITY | CONFIGURATION CONTROL FOR COMPONENT SERVICE / REPAIR','The organization maintains configuration control over [Assignment: organization-defined information system components] awaiting service/repair and serviced/repaired components awaiting return to service.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (692,'COMPONENT AUTHENTICITY | COMPONENT DISPOSAL','The organization disposes of information system components using [Assignment: organization-defined techniques and methods].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (693,'COMPONENT AUTHENTICITY | ANTI-COUNTERFEIT SCANNING','The organization scans for counterfeit information system components [Assignment: organization-defined frequency].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (694,'CUSTOMIZED DEVELOPMENT OF CRITICAL COMPONENTS','The organization re-implements or custom develops [Assignment: organization-defined critical information system components].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (695,'DEVELOPER SCREENING','The organization requires that the developer of [Assignment: organization-defined information system, system component, or information system service]: a. Have appropriate access authorizations as determined by assigned [Assignment: organization-defined official government duties]; and b. Satisfy [Assignment: organization-defined additional personnel screening criteria].\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (696,'DEVELOPER SCREENING | VALIDATION OF SCREENING','The organization requires the developer of the information system, system component, or information system service take [Assignment: organization-defined actions] to ensure that the required access authorizations and screening criteria are satisfied.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (697,'UNSUPPORTED SYSTEM COMPONENTS','The organization: a. Replaces information system components when support for the components is no longer available from the developer, vendor, or manufacturer; and b. Provides justification and documents approval for the continued use of unsupported system components required to satisfy mission/business needs.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (698,'UNSUPPORTED SYSTEM COMPONENTS | ALTERNATIVE SOURCES FOR CONTINUED SUPPORT','The organization provides [Selection (one or more): in-house support; [Assignment: organization-defined support from external providers]] for unsupported information system components.\n		',NULL,15);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (699,'SYSTEM AND COMMUNICATIONS PROTECTION POLICY AND PROCEDURES','The organization:\n			a. Develops, documents, and disseminates to [Assignment: organization-defined personnel or roles]:\n				1. A system and communications protection policy that addresses purpose, scope, roles, responsibilities, management commitment, coordination among organizational entities, and compliance; and\n				2. Procedures to facilitate the implementation of the system and communications protection policy and associated system and communications protection controls; and\n			b. Reviews and updates the current:\n				1. System and communications protection policy [Assignment: organization-defined frequency]; and\n				2. System and communications protection procedures [Assignment: organization-defined frequency].\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (700,'APPLICATION PARTITIONING','The information system separates user functionality (including user interface services) from information system management functionality.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (701,'APPLICATION PARTITIONING | INTERFACES FOR NON-PRIVILEGED USERS','The information system prevents the presentation of information system management-related functionality at an interface for non-privileged users.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (702,'SECURITY FUNCTION ISOLATION','The information system isolates security functions from nonsecurity functions.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (703,'SECURITY FUNCTION ISOLATION | HARDWARE SEPARATION','The information system utilizes underlying hardware separation mechanisms to implement security function isolation.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (704,'SECURITY FUNCTION ISOLATION | ACCESS / FLOW CONTROL FUNCTIONS','The information system isolates security functions enforcing access and information flow control from nonsecurity functions and from other security functions.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (705,'SECURITY FUNCTION ISOLATION | MINIMIZE NONSECURITY FUNCTIONALITY','The organization minimizes the number of nonsecurity functions included within the isolation boundary containing security functions.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (706,'SECURITY FUNCTION ISOLATION | MODULE COUPLING AND COHESIVENESS','The organization implements security functions as largely independent modules that maximize internal cohesiveness within modules and minimize coupling between modules.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (707,'SECURITY FUNCTION ISOLATION | LAYERED STRUCTURES','The organization implements security functions as a layered structure minimizing interactions between layers of the design and avoiding any dependence by lower layers on the functionality or correctness of higher layers.\n		',NULL,16);
