INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (720,'BOUNDARY PROTECTION | EXTERNAL TELECOMMUNICATIONS SERVICES','The organization: (a) Implements a managed interface for each external telecommunication service; (b) Establishes a traffic flow policy for each managed interface; (c) Protects the confidentiality and integrity of the information being transmitted across each interface; (d) Documents each exception to the traffic flow policy with a supporting mission/business need and duration of that need; and (e) Reviews exceptions to the traffic flow policy [Assignment: organization-defined frequency] and removes exceptions that are no longer supported by an explicit mission/business need.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (721,'BOUNDARY PROTECTION | DENY BY DEFAULT / ALLOW BY EXCEPTION','The information system at managed interfaces denies network communications traffic by default and allows network communications traffic by exception (i.e., deny all, permit by exception).\n		',NULL,16);
