INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (723,'BOUNDARY PROTECTION | PREVENT SPLIT TUNNELING FOR REMOTE DEVICES','The information system, in conjunction with a remote device, prevents the device from simultaneously establishing non-remote connections with the system and communicating via some other connection to resources in external networks.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (724,'BOUNDARY PROTECTION | ROUTE TRAFFIC TO AUTHENTICATED PROXY SERVERS','The information system routes [Assignment: organization-defined internal communications traffic] to [Assignment: organization-defined external networks] through authenticated proxy servers at managed interfaces.\n		',NULL,16);
