INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (801,'CONCEALMENT AND MISDIRECTION | CHANGE PROCESSING / STORAGE LOCATIONS','The organization changes the location of [Assignment: organization-defined processing and/or storage] [Selection: [Assignment: organization-defined time frequency]; at random time intervals]].\n		',NULL,16);
