INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (806,'COVERT CHANNEL ANALYSIS | MAXIMUM BANDWIDTH','The organization reduces the maximum bandwidth for identified covert [Selection (one or more); storage; timing] channels to [Assignment: organization-defined values].\n		',NULL,16);
