INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (810,'NON-MODIFIABLE EXECUTABLE PROGRAMS','The information system at [Assignment: organization-defined information system components]: a. Loads and executes the operating environment from hardware-enforced, read-only media; and b. Loads and executes [Assignment: organization-defined applications] from hardware-enforced, read-only media.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (811,'NON-MODIFIABLE EXECUTABLE PROGRAMS | NO WRITABLE STORAGE','The organization employs [Assignment: organization-defined information system components] with no writeable storage that is persistent across component restart or power on/off.\n		',NULL,16);
