INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (813,'NON-MODIFIABLE EXECUTABLE PROGRAMS | HARDWARE-BASED PROTECTION','The organization: (a) Employs hardware-based, write-protect for [Assignment: organization-defined information system firmware components]; and (b) Implements specific procedures for [Assignment: organization-defined authorized individuals] to manually disable hardware write-protect for firmware modifications and re-enable the write-protect prior to returning to operational mode.\n		',NULL,16);
INSERT INTO `` (`idCONTROL`,`name`,`description`,`maturity_level`,`FAMILY_idFAMILY`) VALUES (814,'HONEYCLIENTS','The information system includes components that proactively seek to identify malicious websites and/or web-based malicious code.\n		',NULL,16);
