INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (24,'Inventory and Control of Enterprise Assets','Actively manage (inventory, track, and correct) all enterprise assets (end-user devices, including portable and mobile; network devices; non-computing/Internet of Things (IoT) devices; and servers) connected to the infrastructure physically, virtually, remotely, and those within cloud environments, to accurately know the totality of assets that need to be monitored and protected within the enterprise. This will also support identifying unauthorized and unmanaged assets to remove or remediate.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (25,'Inventory and Control of Software Assets','Actively manage (inventory, track, and correct) all software (operating systems and applications) on the network so that only authorized software is installed and can execute, and that unauthorized and unmanaged software is found and prevented from installation or execution.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (26,'Data Protection','Develop processes and technical controls to identify, classify, securely handle, retain, and dispose of data.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (27,'Secure Configuration of Enterprise Assets and Software','Establish and maintain the secure configuration of enterprise assets (end-user devices, including portable and mobile; network devices; non-computing/IoT devices; and servers) and software (operating systems and applications).',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (28,'Account Management','Use processes and tools to assign and manage authorization to credentials for user accounts, including administrator accounts, as well as service accounts, to enterprise assets and software.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (29,'Access Control Management','Use processes and tools to create, assign, manage, and revoke access credentials and privileges for user, administrator, and service accounts for enterprise assets and software.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (30,'Continuous Vulnerability Management','Develop a plan to continuously assess and track vulnerabilities on all enterprise assets within the enterprise’s infrastructure, in order to remediate, and minimize, the window of opportunity for attackers. Monitor public and private industry sources for new threat and vulnerability information.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (31,'Audit Log Management','Collect, alert, review, and retain audit logs of events that could help detect, understand, or recover from an attack.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (32,'Email and Web Browser Protections','Improve protections and detections of threats from email and web vectors, as these are opportunities for attackers to manipulate human behavior through direct engagement.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (33,'Malware Defenses','Prevent or control the installation, spread, and execution of malicious applications, code, or scripts on enterprise assets.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (34,'Data Recovery','Establish and maintain data recovery practices sufficient to restore in-scope enterprise assets to a pre-incident and trusted state.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (35,'Network Infrastructure Management','Establish, implement, and actively manage (track, report, correct) network devices, in order to prevent attackers from exploiting vulnerable network services and access points.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (36,'Network Monitoring and Defense','Operate processes and tooling to establish and maintain comprehensive network monitoring and defense against security threats across the enterprise’s network infrastructure and user base.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (37,'Security Awareness and Skills Training','Establish and maintain a security awareness program to influence behavior among the workforce to be security conscious and properly skilled to reduce cybersecurity risks to the enterprise.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (38,'Service Provider Management','Develop a process to evaluate service providers who hold sensitive data, or are responsible for an enterprise’s critical IT platforms or processes, to ensure these providers are protecting those platforms and data appropriately.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (39,'Application Software Security','Manage the security life cycle of in-house developed, hosted, or acquired software to prevent, detect, and remediate security weaknesses before they can impact the enterprise.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (40,'Incident Response Management','Establish a program to develop and maintain an incident response capability (e.g., policies, plans, procedures, defined roles, training, and communications) to prepare, detect, and quickly respond to an attack.',1);
INSERT INTO `` (`idFAMILY`,`name`,`description`,`FRAMEWORK_idFRAMEWORK`) VALUES (41,'Penetration Testing','Test the effectiveness and resiliency of enterprise assets through identifying and exploiting weaknesses in controls (people, processes, and technology), and simulating the objectives and actions of an attacker.',1);
